import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';
import { FaBriefcase, FaCalendarAlt, FaMapMarkerAlt, FaTrophy } from 'react-icons/fa';

const ExperienceContainer = styled.section`
  padding: 6rem 2rem;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
  border-top: 1px solid rgba(255, 255, 255, 0.1);
`;

const SectionTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: 700;
  color: white;
  text-align: center;
  margin-bottom: 1rem;
  background: linear-gradient(45deg, #ffd700, #ffed4e);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const SectionSubtitle = styled(motion.p)`
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.8);
  text-align: center;
  margin-bottom: 4rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const Timeline = styled.div`
  max-width: 800px;
  margin: 0 auto;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 2px;
    background: linear-gradient(to bottom, #ffd700, #ffed4e);
    transform: translateX(-50%);

    @media (max-width: 768px) {
      left: 20px;
    }
  }
`;

const TimelineItem = styled(motion.div)<{ isLeft: boolean }>`
  position: relative;
  margin-bottom: 3rem;
  width: 50%;
  
  ${props => props.isLeft ? `
    left: 0;
    padding-right: 3rem;
    text-align: right;

    @media (max-width: 768px) {
      left: 0;
      width: 100%;
      padding-right: 0;
      padding-left: 3rem;
      text-align: left;
    }
  ` : `
    left: 50%;
    padding-left: 3rem;

    @media (max-width: 768px) {
      left: 0;
      width: 100%;
      padding-left: 3rem;
    }
  `}
`;

const TimelineDot = styled.div<{ isLeft: boolean }>`
  position: absolute;
  top: 1rem;
  width: 20px;
  height: 20px;
  background: #ffd700;
  border-radius: 50%;
  border: 4px solid white;
  box-shadow: 0 0 0 4px rgba(255, 215, 0, 0.3);
  
  ${props => props.isLeft ? `
    right: -10px;

    @media (max-width: 768px) {
      left: 10px;
      right: auto;
    }
  ` : `
    left: -10px;

    @media (max-width: 768px) {
      left: 10px;
    }
  `}
`;

const ExperienceCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(10px);
  position: relative;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
    border-color: #ffd700;
  }
`;

const JobTitle = styled.h3`
  font-size: 1.4rem;
  font-weight: 600;
  color: white;
  margin-bottom: 0.5rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const CompanyName = styled.h4`
  font-size: 1.1rem;
  color: #ffd700;
  margin-bottom: 0.5rem;
  font-weight: 500;
`;

const JobDuration = styled.div`
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.9rem;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const JobDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
  margin-bottom: 1rem;
`;

const AchievementsList = styled.ul`
  list-style: none;
  padding: 0;
  margin-bottom: 1rem;
`;

const AchievementItem = styled.li`
  color: rgba(255, 255, 255, 0.8);
  padding: 0.25rem 0;
  position: relative;
  padding-left: 1.5rem;

  &::before {
    content: 'â–¶';
    position: absolute;
    left: 0;
    color: #ffd700;
    font-size: 0.8rem;
  }
`;

const TechnologiesList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const TechnologyTag = styled.span`
  background: rgba(255, 215, 0, 0.2);
  color: #ffd700;
  padding: 0.25rem 0.75rem;
  border-radius: 15px;
  font-size: 0.8rem;
  font-weight: 500;
  border: 1px solid rgba(255, 215, 0, 0.3);
`;

const Experience: React.FC = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });

  const experiences = [
    {
      id: 1,
      title: "Senior Data Analyst",
      company: "TechCorp Solutions",
      duration: "2022 - Present",
      location: "San Francisco, CA",
      description: "Leading data analysis initiatives and developing advanced analytics solutions for enterprise clients. Responsible for building predictive models and creating interactive dashboards that drive business decisions.",
      achievements: [
        "Increased data processing efficiency by 40% through automation",
        "Developed ML models with 92% accuracy for customer churn prediction",
        "Led team of 5 analysts in delivering 15+ major projects",
        "Reduced reporting time by 60% through automated dashboards"
      ],
      technologies: ["Python", "Power BI", "SQL", "Machine Learning", "Azure", "Tableau"]
    },
    {
      id: 2,
      title: "Business Intelligence Developer",
      company: "DataFlow Inc",
      duration: "2020 - 2022",
      location: "New York, NY",
      description: "Designed and implemented comprehensive BI solutions using modern data visualization tools. Collaborated with cross-functional teams to deliver data-driven insights and improve operational efficiency.",
      achievements: [
        "Built 20+ interactive dashboards serving 500+ users",
        "Optimized database queries resulting in 50% faster load times",
        "Implemented real-time data streaming for live analytics",
        "Trained 30+ users on self-service analytics tools"
      ],
      technologies: ["Tableau", "SQL Server", "R", "D3.js", "AWS", "Excel"]
    },
    {
      id: 3,
      title: "Data Analyst",
      company: "Analytics Pro",
      duration: "2018 - 2020",
      location: "Chicago, IL",
      description: "Performed statistical analysis and created reports to support business operations. Developed data collection processes and maintained data quality standards across multiple departments.",
      achievements: [
        "Created automated reporting system reducing manual work by 70%",
        "Identified cost-saving opportunities worth $2M annually",
        "Improved data accuracy from 85% to 98%",
        "Developed forecasting models for inventory management"
      ],
      technologies: ["Excel", "SQL", "Python", "SAS", "Google Analytics", "VBA"]
    }
  ];

  return (
    <ExperienceContainer id="experience" ref={ref}>
      <SectionTitle
        initial={{ opacity: 0, y: 30 }}
        animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.6 }}
      >
        Professional Experience
      </SectionTitle>
      
      <SectionSubtitle
        initial={{ opacity: 0, y: 30 }}
        animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.6, delay: 0.2 }}
      >
        A journey of growth and impact in data analytics and business intelligence
      </SectionSubtitle>

      <Timeline>
        {experiences.map((experience, index) => (
          <TimelineItem
            key={experience.id}
            isLeft={index % 2 === 0}
            initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
            animate={isInView ? { opacity: 1, x: 0 } : { opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
            transition={{ duration: 0.6, delay: index * 0.2 }}
          >
            <TimelineDot isLeft={index % 2 === 0} />
            <ExperienceCard
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              <JobTitle>
                <FaBriefcase />
                {experience.title}
              </JobTitle>
              <CompanyName>{experience.company}</CompanyName>
              <JobDuration>
                <FaCalendarAlt />
                {experience.duration}
                <FaMapMarkerAlt style={{ marginLeft: '1rem' }} />
                {experience.location}
              </JobDuration>
              
              <JobDescription>{experience.description}</JobDescription>
              
              <AchievementsList>
                {experience.achievements.map((achievement, achIndex) => (
                  <AchievementItem key={achIndex}>
                    {achievement}
                  </AchievementItem>
                ))}
              </AchievementsList>
              
              <TechnologiesList>
                {experience.technologies.map((tech, techIndex) => (
                  <TechnologyTag key={techIndex}>
                    {tech}
                  </TechnologyTag>
                ))}
              </TechnologiesList>
            </ExperienceCard>
          </TimelineItem>
        ))}
      </Timeline>
    </ExperienceContainer>
  );
};

export default Experience;
