import React, { useRef, useEffect, useState, useCallback } from 'react';
import styled from 'styled-components';
import { motion, useScroll, useTransform, useMotionValue, useSpring } from 'framer-motion';

const FloatingDataFlowContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  pointer-events: none;
  z-index: 1;
  overflow: hidden;
`;

const DataIcon = styled(motion.div)<{ 
  size: number;
  color: string;
}>`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: ${props => props.size * 0.6}px;
  background: linear-gradient(135deg, 
    ${props => props.color}20, 
    ${props => props.color}40
  );
  border: 2px solid ${props => props.color}60;
  box-shadow: 
    0 0 ${props => props.size}px ${props => props.color}30,
    inset 0 0 ${props => props.size * 0.3}px ${props => props.color}20;
  cursor: pointer;
  pointer-events: all;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;
  
  &:hover {
    transform: scale(1.2);
    box-shadow: 
      0 0 ${props => props.size * 1.5}px ${props => props.color}50,
      inset 0 0 ${props => props.size * 0.5}px ${props => props.color}30;
  }
`;

const ConnectionLine = styled(motion.div)<{ 
  from: { x: number; y: number }; 
  to: { x: number; y: number };
  color: string;
}>`
  position: absolute;
  height: 2px;
  background: linear-gradient(90deg, 
    ${props => props.color}80, 
    ${props => props.color}40,
    ${props => props.color}80
