const FloatingDataFlow: React.FC = () => {
  const containerRef = useRef<HTMLDivElement>(null);
  const [hoveredIcon, setHoveredIcon] = useState<string | null>(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [connections, setConnections] = useState<Array<{
    from: { x: number; y: number };
    to: { x: number; y: number };
    color: string;
  }>>([]);
  
  const { scrollYProgress } = useScroll();
  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);
  
  const springX = useSpring(mouseX, { stiffness: 150, damping: 15 });
  const springY = useSpring(mouseY, { stiffness: 150, damping: 15 });
  
  // Update mouse position
  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      const x = e.clientX / window.innerWidth;
      const y = e.clientY / window.innerHeight;
      setMousePosition({ x, y });
      mouseX.set(e.clientX);
      mouseY.set(e.clientY);
    };
    
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [mouseX, mouseY]);
  
  // Generate connections based on scroll progress
  useEffect(() => {
    const newConnections: Array<{
      from: { x: number; y: number };
      to: { x: number; y: number };
      color: string;
    }> = [];
    
    const progress = scrollYProgress.get();
    const connectionThreshold = 0.3; // Start showing connections at 30% scroll
    
    if (progress > connectionThreshold) {
      dataFlowConfig.icons.forEach(icon => {
        icon.connections.forEach(connectionId => {
          const targetIcon = dataFlowConfig.icons.find(i => i.id === connectionId);
          if (targetIcon) {
            newConnections.push({
              from: icon.position,
              to: targetIcon.position,
              color: icon.color
            });
          }
        });
      });
    }
    
    setConnections(newConnections);
  }, [scrollYProgress]);
  
  // Calculate magnetic attraction
  const getMagneticPosition = useCallback((icon: any) => {
    const baseX = icon.position.x * window.innerWidth;
    const baseY = icon.position.y * window.innerHeight;
    
    const mouseDistance = Math.sqrt(
      Math.pow(mousePosition.x * window.innerWidth - baseX, 2) +
      Math.pow(mousePosition.y * window.innerHeight - baseY, 2)
    );
    
    const attractionRadius = 150;
    const attractionStrength = 0.3;
    
    if (mouseDistance < attractionRadius) {
      const attraction = (attractionRadius - mouseDistance) / attractionRadius;
      const deltaX = (mousePosition.x * window.innerWidth - baseX) * attraction * attractionStrength;
      const deltaY = (mousePosition.y * window.innerHeight - baseY) * attraction * attractionStrength;
      
      return {
        x: baseX + deltaX,
        y: baseY + deltaY
      };
    }
    
    return { x: baseX, y: baseY };
  }, [mousePosition]);
  
  return (
    <FloatingDataFlowContainer ref={containerRef}>
      {/* Connection Lines */}
      {connections.map((connection, index) => {
        const fromPos = getMagneticPosition({ position: connection.from });
        const toPos = getMagneticPosition({ position: connection.to });
        
        const length = Math.sqrt(
          Math.pow(toPos.x - fromPos.x, 2) + Math.pow(toPos.y - fromPos.y, 2)
        );
        const angle = Math.atan2(toPos.y - fromPos.y, toPos.x - fromPos.x) * 180 / Math.PI;
        
        return (
          <ConnectionLine
            key={index}
            from={connection.from}
            to={connection.to}
            color={connection.color}
            initial={{ scaleX: 0 }}
            animate={{ scaleX: 1 }}
            transition={{ duration: 0.8, delay: index * 0.1 }}
            style={{
              left: fromPos.x,
              top: fromPos.y,
              width: length,
              transform: `rotate(${angle}deg)`
            }}
          />
        );
      })}
      
      {/* Data Icons */}
      {dataFlowConfig.icons.map((icon, index) => {
        const magneticPos = getMagneticPosition(icon);
        
        return (
          <DataIcon
            key={icon.id}
            size={icon.size}
            color={icon.color}
            initial={{ 
              scale: 0, 
              rotate: -180,
              opacity: 0
            }}
            animate={{ 
              scale: 1, 
              rotate: 0,
              opacity: 1
            }}
            transition={{ 
              duration: 0.6, 
              delay: index * 0.1,
              type: "spring",
              stiffness: 100
            }}
            whileHover={{ 
              scale: 1.3,
              rotate: 10,
              zIndex: 1000
            }}
            whileTap={{ scale: 0.9 }}
            style={{
              left: magneticPos.x - icon.size / 2,
              top: magneticPos.y - icon.size / 2,
            }}
            onHoverStart={() => setHoveredIcon(icon.id)}
            onHoverEnd={() => setHoveredIcon(null)}
          >
            {icon.icon}
          </DataIcon>
        );
      })}
      
      {/* Data Preview */}
      {hoveredIcon && (
        <DataPreview
          color={dataFlowConfig.icons.find(i => i.id === hoveredIcon)?.color || '#40e0ff'}
          initial={{ opacity: 0, scale: 0.8, y: 20 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          exit={{ opacity: 0, scale: 0.8, y: 20 }}
          transition={{ duration: 0.2 }}
          style={{
            left: mousePosition.x * window.innerWidth + 20,
            top: mousePosition.y * window.innerHeight - 100
          }}
        >
          {(() => {
            const icon = dataFlowConfig.icons.find(i => i.id === hoveredIcon);
            if (!icon) return null;
            
            return (
              <>
                <PreviewTitle>{icon.preview.title}</PreviewTitle>
                <PreviewDescription>{icon.preview.description}</PreviewDescription>
                <PreviewStats>
                  {icon.preview.stats.map((stat, index) => (
                    <Stat key={index} color={icon.color}>
                      {stat}
                    </Stat>
                  ))}
                </PreviewStats>
              </>
            );
          })()}
        </DataPreview>
      )}
    </FloatingDataFlowContainer>
  );
};

export default FloatingDataFlow;
