import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaPlus, FaEdit, FaTrash, FaSave, FaTimes } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

interface Experience {
  id: number;
  title: string;
  company: string;
  duration: string;
  location: string;
  description: string;
  achievements: string[];
  technologies: string[];
}

const EditorContainer = styled.div`
  h2 {
    color: #333;
    margin-bottom: 1.5rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }
`;

const ExperiencesList = styled.div`
  display: grid;
  gap: 1rem;
  margin-bottom: 2rem;
`;

const ExperienceCard = styled(motion.div)`
  background: #f8f9fa;
  border: 1px solid #e9ecef;
  border-radius: 10px;
  padding: 1.5rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  transition: all 0.3s ease;

  &:hover {
    border-color: #ffd700;
    box-shadow: 0 5px 15px rgba(255, 215, 0, 0.1);
  }
`;

const ExperienceInfo = styled.div`
  flex: 1;
`;

const ExperienceTitle = styled.h3`
  color: #333;
  margin-bottom: 0.5rem;
  font-size: 1.1rem;
`;

const ExperienceCompany = styled.p`
  color: #666;
  font-size: 0.9rem;
  margin-bottom: 0.5rem;
  font-weight: 600;
`;

const ExperienceDuration = styled.p`
  color: #666;
  font-size: 0.9rem;
  margin-bottom: 0.5rem;
`;

const ExperienceTechnologies = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.25rem;
`;

const TechTag = styled.span`
  background: #e9ecef;
  color: #495057;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  font-size: 0.8rem;
`;

const ExperienceActions = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ActionButton = styled(motion.button)<{ variant?: 'edit' | 'delete' | 'add' }>`
  padding: 0.5rem;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  
  ${props => {
    switch (props.variant) {
      case 'edit':
        return `
          background: #007bff;
          color: white;
          &:hover { background: #0056b3; }
        `;
      case 'delete':
        return `
          background: #dc3545;
          color: white;
          &:hover { background: #c82333; }
        `;
      case 'add':
        return `
          background: #28a745;
          color: white;
          &:hover { background: #218838; }
        `;
      default:
        return `
          background: #6c757d;
          color: white;
          &:hover { background: #5a6268; }
        `;
    }
  }}
`;

const FormContainer = styled(motion.div)`
  background: #f8f9fa;
  border: 1px solid #e9ecef;
  border-radius: 10px;
  padding: 2rem;
  margin-bottom: 2rem;
`;

const Form = styled.form`
  display: grid;
  gap: 1.5rem;
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Label = styled.label`
  font-weight: 600;
  color: #333;
  font-size: 0.9rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #ced4da;
  border-radius: 6px;
  font-size: 1rem;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #ffd700;
    box-shadow: 0 0 0 2px rgba(255, 215, 0, 0.1);
  }
`;

const TextArea = styled.textarea`
  padding: 0.75rem;
  border: 1px solid #ced4da;
  border-radius: 6px;
  font-size: 1rem;
  min-height: 100px;
  resize: vertical;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #ffd700;
    box-shadow: 0 0 0 2px rgba(255, 215, 0, 0.1);
  }
`;

const TechInput = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  padding: 0.75rem;
  border: 1px solid #ced4da;
  border-radius: 6px;
  min-height: 50px;
  align-items: center;
`;

const TechTagInput = styled.span`
  background: #007bff;
  color: white;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  font-size: 0.8rem;
  display: flex;
  align-items: center;
  gap: 0.25rem;
`;

const TechRemove = styled.button`
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  font-size: 0.8rem;
  margin-left: 0.25rem;

  &:hover {
    color: #ffc107;
  }
`;

const TechAddInput = styled.input`
  border: none;
  outline: none;
  flex: 1;
  min-width: 100px;
`;

const AchievementInput = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  padding: 0.75rem;
  border: 1px solid #ced4da;
  border-radius: 6px;
  min-height: 50px;
  align-items: center;
`;

const AchievementTag = styled.span`
  background: #28a745;
  color: white;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  font-size: 0.8rem;
  display: flex;
  align-items: center;
  gap: 0.25rem;
`;

const AchievementRemove = styled.button`
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  font-size: 0.8rem;
  margin-left: 0.25rem;

  &:hover {
    color: #ffc107;
  }
`;

const AchievementAddInput = styled.input`
  border: none;
  outline: none;
  flex: 1;
  min-width: 100px;
`;

const FormActions = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: flex-end;
`;

const SaveButton = styled(motion.button)`
  background: #28a745;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 6px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    background: #218838;
    transform: translateY(-2px);
  }
`;

const CancelButton = styled(motion.button)`
  background: #6c757d;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 6px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    background: #5a6268;
    transform: translateY(-2px);
  }
`;

interface ExperienceEditorProps {
  onEdit: (id: number | null) => void;
  editingId: number | null;
  onClose: () => void;
}

const ExperienceEditor: React.FC<ExperienceEditorProps> = ({ onEdit, editingId, onClose }) => {
  const { experiences, addExperience, updateExperience, deleteExperience } = useData();
  const [isAdding, setIsAdding] = useState(false);
  const [formData, setFormData] = useState<Partial<Experience>>({
    title: '',
    company: '',
    duration: '',
    location: '',
    description: '',
    achievements: [],
    technologies: []
  });
  const [newTech, setNewTech] = useState('');
  const [newAchievement, setNewAchievement] = useState('');

  useEffect(() => {
    if (editingId) {
      const experience = experiences.find(e => e.id === editingId);
      if (experience) {
        setFormData(experience);
        setIsAdding(false);
      }
    } else {
      setIsAdding(false);
    }
  }, [editingId, experiences]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (isAdding) {
      addExperience(formData as Experience);
    } else if (editingId) {
      updateExperience(editingId, formData as Experience);
    }
    
    handleClose();
  };

  const handleClose = () => {
    setFormData({
      title: '',
      company: '',
      duration: '',
      location: '',
      description: '',
      achievements: [],
      technologies: []
    });
    setIsAdding(false);
    onClose();
  };

  const handleAddTech = () => {
    if (newTech.trim()) {
      setFormData(prev => ({
        ...prev,
        technologies: [...(prev.technologies || []), newTech.trim()]
      }));
      setNewTech('');
    }
  };

  const handleRemoveTech = (index: number) => {
    setFormData(prev => ({
      ...prev,
      technologies: prev.technologies?.filter((_, i) => i !== index) || []
    }));
  };

  const handleAddAchievement = () => {
    if (newAchievement.trim()) {
      setFormData(prev => ({
        ...prev,
        achievements: [...(prev.achievements || []), newAchievement.trim()]
      }));
      setNewAchievement('');
    }
  };

  const handleRemoveAchievement = (index: number) => {
    setFormData(prev => ({
      ...prev,
      achievements: prev.achievements?.filter((_, i) => i !== index) || []
    }));
  };

  return (
    <EditorContainer>
      <h2>Manage Experience</h2>

      <ActionButton
        variant="add"
        onClick={() => setIsAdding(true)}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        style={{ marginBottom: '1rem' }}
      >
        <FaPlus />
        Add New Experience
      </ActionButton>

      {(isAdding || editingId) && (
        <FormContainer
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
        >
          <Form onSubmit={handleSubmit}>
            <FormRow>
              <FormGroup>
                <Label>Job Title</Label>
                <Input
                  type="text"
                  value={formData.title || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>Company</Label>
                <Input
                  type="text"
                  value={formData.company || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, company: e.target.value }))}
                  required
                />
              </FormGroup>
            </FormRow>

            <FormRow>
              <FormGroup>
                <Label>Duration</Label>
                <Input
                  type="text"
                  value={formData.duration || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, duration: e.target.value }))}
                  placeholder="e.g., 2022 - Present"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>Location</Label>
                <Input
                  type="text"
                  value={formData.location || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, location: e.target.value }))}
                  placeholder="e.g., San Francisco, CA"
                />
              </FormGroup>
            </FormRow>

            <FormGroup>
              <Label>Description</Label>
              <TextArea
                value={formData.description || ''}
                onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label>Achievements</Label>
              <AchievementInput>
                {(formData.achievements || []).map((achievement, index) => (
                  <AchievementTag key={index}>
                    {achievement}
                    <AchievementRemove onClick={() => handleRemoveAchievement(index)}>
                      ×
                    </AchievementRemove>
                  </AchievementTag>
                ))}
                <AchievementAddInput
                  type="text"
                  value={newAchievement}
                  onChange={(e) => setNewAchievement(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddAchievement())}
                  placeholder="Add achievement..."
                />
              </AchievementInput>
            </FormGroup>

            <FormGroup>
              <Label>Technologies</Label>
              <TechInput>
                {(formData.technologies || []).map((tech, index) => (
                  <TechTagInput key={index}>
                    {tech}
                    <TechRemove onClick={() => handleRemoveTech(index)}>
                      ×
                    </TechRemove>
                  </TechTagInput>
                ))}
                <TechAddInput
                  type="text"
                  value={newTech}
                  onChange={(e) => setNewTech(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddTech())}
                  placeholder="Add technology..."
                />
              </TechInput>
            </FormGroup>

            <FormActions>
              <SaveButton
                type="submit"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaSave />
                {isAdding ? 'Add Experience' : 'Update Experience'}
              </SaveButton>
              <CancelButton
                type="button"
                onClick={handleClose}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaTimes />
                Cancel
              </CancelButton>
            </FormActions>
          </Form>
        </FormContainer>
      )}

      <ExperiencesList>
        {experiences.map((experience) => (
          <ExperienceCard
            key={experience.id}
            whileHover={{ scale: 1.02 }}
            transition={{ duration: 0.2 }}
          >
            <ExperienceInfo>
              <ExperienceTitle>{experience.title}</ExperienceTitle>
              <ExperienceCompany>{experience.company}</ExperienceCompany>
              <ExperienceDuration>{experience.duration} • {experience.location}</ExperienceDuration>
              <ExperienceTechnologies>
                {experience.technologies.map((tech, index) => (
                  <TechTag key={index}>{tech}</TechTag>
                ))}
              </ExperienceTechnologies>
            </ExperienceInfo>
            <ExperienceActions>
              <ActionButton
                variant="edit"
                onClick={() => onEdit(experience.id)}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <FaEdit />
              </ActionButton>
              <ActionButton
                variant="delete"
                onClick={() => deleteExperience(experience.id)}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <FaTrash />
              </ActionButton>
            </ExperienceActions>
          </ExperienceCard>
        ))}
      </ExperiencesList>
    </EditorContainer>
  );
};

export default ExperienceEditor;
