import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaMapMarkerAlt, FaEnvelope, FaPhone, FaChevronDown, FaLinkedin } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #1a1a2e 100%);
  color: #ffffff;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  position: relative;
  overflow: hidden;
`;

const HeroContent = styled.div`
  display: flex;
  align-items: center;
  gap: 4rem;
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
  z-index: 2;
  position: relative;

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
    gap: 2rem;
  }
`;

const ProfileSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const ProfileImageContainer = styled(motion.div)`
  width: 480px;
  height: 480px;
  border-radius: 50%;
  overflow: hidden;
  position: relative;
  margin-bottom: 2rem;
  background: linear-gradient(45deg, 
    rgba(64, 224, 255, 0.3) 0%, 
    rgba(100, 200, 255, 0.2) 50%, 
    rgba(64, 224, 255, 0.3) 100%
  );
  backdrop-filter: blur(10px);
  border: 6px solid rgba(64, 224, 255, 0.3);
  box-shadow: 
    0 20px 40px rgba(0, 0, 0, 0.3),
    inset 0 0 20px rgba(64, 224, 255, 0.1);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 8rem;
  color: rgba(64, 224, 255, 0.6);
`;

const TextSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
`;

const Greeting = styled(motion.h1)`
  font-size: 1.2rem;
  font-weight: 300;
  color: rgba(64, 224, 255, 0.8);
  margin-bottom: 0.5rem;
  letter-spacing: 1px;
  font-weight: 200;
  overflow: hidden;
  white-space: nowrap;
  display: block;
  text-align: left;
`;

const Name = styled(motion.h2)`
  font-size: 4rem;
  font-weight: 100;
  margin-bottom: 1rem;
  background: linear-gradient(45deg, 
    #ffffff 0%, 
    rgba(64, 224, 255, 0.9) 50%, 
    #ffffff 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.1;
  letter-spacing: -2px;
  display: block;
  overflow: hidden;
  text-align: left;

  @media (max-width: 768px) {
    font-size: 3rem;
  }
`;

const Title = styled(motion.h3)`
  font-size: 1.5rem;
  font-weight: 300;
  color: rgba(255, 255, 255, 0.7);
  margin-bottom: 1.5rem;
  text-align: left;
  min-height: 2.5rem;
  display: flex;
  align-items: flex-start;
`;

const Bio = styled(motion.p)`
  font-size: 1.1rem;
  line-height: 1.8;
  color: rgba(255, 255, 255, 0.6);
  margin-bottom: 2rem;
  text-align: left;
  width: 100%;
`;

const ContactInfo = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  align-items: flex-start;
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.95rem;
  padding: 0.25rem 0.5rem;
`;

const ContactLink = styled.a`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  color: rgba(255, 255, 255, 0.7);
  text-decoration: none;
  font-size: 0.95rem;
  padding: 0.25rem 0.5rem;
  border-radius: 6px;
  transition: all 0.3s ease;
  cursor: pointer;

  &:hover {
    color: #40e0ff;
    background: rgba(64, 224, 255, 0.1);
    transform: translateX(4px);

    &::after {
      content: ' ↗';
      font-size: 0.8rem;
      opacity: 0.7;
    }
  }
`;

const ContactIcon = styled.span`
  color: #40e0ff;
  font-size: 1rem;
`;

const Hero: React.FC = () => {
  const { profile } = useData();

  return (
    <HeroContainer id="home">
      <HeroContent>
        <ProfileSection>
          <ProfileImageContainer
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 0.8, type: "spring", stiffness: 100 }}
          >
            <span style={{ fontSize: '8rem', color: 'rgba(64, 224, 255, 0.6)' }}>👨‍💻</span>
          </ProfileImageContainer>
        </ProfileSection>

        <TextSection>
          <Greeting
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Hi, I'm
          </Greeting>

          <Name
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            {profile.name || "Your Name"}
          </Name>

          <Title
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Data Analyst & BI Developer
          </Title>

          <Bio
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            Passionate data analyst with expertise in transforming complex data into actionable insights. Specializing in business intelligence, machine learning, and data visualization to drive strategic decision-making.
          </Bio>

          <ContactInfo
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <ContactItem>
              <ContactIcon><FaMapMarkerAlt /></ContactIcon>
              <span>{profile.location || "Your City, Country"}</span>
            </ContactItem>

            <ContactLink href={`mailto:${profile.email || "your.email@example.com"}`}>
              <ContactIcon><FaEnvelope /></ContactIcon>
              <span>{profile.email || "your.email@example.com"}</span>
            </ContactLink>

            <ContactItem>
              <ContactIcon><FaPhone /></ContactIcon>
              <span>{profile.phone || "+1 (555) 123-4567"}</span>
            </ContactItem>

            <ContactLink 
              href={profile.linkedin || "https://linkedin.com/in/your-profile"} 
              target="_blank" 
              rel="noopener noreferrer"
            >
              <ContactIcon><FaLinkedin /></ContactIcon>
              <span>LinkedIn Profile</span>
            </ContactLink>
          </ContactInfo>
        </TextSection>
      </HeroContent>
    </HeroContainer>
  );
};

export default Hero;
