const Skills: React.FC = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });
  const { skills } = useData();

  // Group skills by category
  const skillsByCategory = skills.reduce((acc, skill) => {
    if (!acc[skill.category]) {
      acc[skill.category] = [];
    }
    acc[skill.category].push(skill);
    return acc;
  }, {} as Record<string, any[]>);

  const categoryIcons = {
    'Programming & Query Languages': <FaCode />,
    'Analytics & Data Platforms': <FaChartBar />,
    'Application Development & Automation': <FaCogs />,
    'Business Systems': <FaBuilding />
  };

  const categoryOrder = ['Programming & Query Languages', 'Analytics & Data Platforms', 'Application Development & Automation', 'Business Systems'];

  // Helper function to get proficiency dots (1-3 dots)
  const getProficiencyDots = (level: number) => {
    if (level >= 90) return 3;
    if (level >= 60) return 2;
    return 1;
  };

  return (
    <SkillsContainer id="skills" ref={ref}>
      <SectionTitle
        initial={{ opacity: 0, y: 30 }}
        animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.6 }}
      >
        Technical Skills
      </SectionTitle>

      <SectionSubtitle
        initial={{ opacity: 0, y: 30 }}
        animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.6, delay: 0.2 }}
      >
        Comprehensive expertise in data analysis, visualization, and business intelligence
      </SectionSubtitle>

      <SkillsGrid>
        {categoryOrder.map((categoryName, index) => {
          const categorySkills = skillsByCategory[categoryName] || [];
          if (categorySkills.length === 0) return null;

          return (
            <SkillCategory
              key={categoryName}
              category={categoryName}
              initial={{ opacity: 0, y: 50, rotateX: -15 }}
              animate={isInView ? { 
                opacity: 1, 
                y: 0, 
                rotateX: 0,
                scale: 1
              } : { 
                opacity: 0, 
                y: 50, 
                rotateX: -15,
                scale: 0.9
              }}
              transition={{ 
                duration: 0.8, 
                delay: index * 0.2,
                ease: "easeOut"
              }}
              whileHover={{ 
                scale: 1.05,
                rotateY: 5,
                z: 50
              }}
              whileTap={{ scale: 0.95 }}
            >
              <CategoryTitle>
                <CategoryIcon category={categoryName}>
                  {categoryIcons[categoryName as keyof typeof categoryIcons]}
                </CategoryIcon>
                {categoryName}
              </CategoryTitle>
            <SkillsList>
              {categorySkills
                .sort((a, b) => b.level - a.level) // Sort by proficiency (highest first)
                .map((skill, skillIndex) => {
                  const dots = getProficiencyDots(skill.level);
                  return (
                    <SkillItem
                      key={skill.name}
                      category={categoryName}
                      proficiency={skill.level}
                      initial={{ opacity: 0, scale: 0.8 }}
                      animate={isInView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                      transition={{ 
                        duration: 0.3, 
                        delay: index * 0.1 + skillIndex * 0.05 
                      }}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      <SkillContent>
                        <SkillName>{skill.name}</SkillName>
                        <ProficiencyDots>
                          {[1, 2, 3].map((dotNumber) => (
                            <Dot
                              key={dotNumber}
                              filled={dotNumber <= dots}
                              category={categoryName}
                              isExpert={skill.level >= 90}
                            />
                          ))}
                        </ProficiencyDots>
                      </SkillContent>
                    </SkillItem>
                  );
                })}
            </SkillsList>
            </SkillCategory>
          );
        })}
      </SkillsGrid>
    </SkillsContainer>
  );
};

export default Skills;
