import React, { useState, useEffect } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { motion } from 'framer-motion';
import { FaMapMarkerAlt, FaEnvelope, FaPhone, FaDownload, FaChevronDown } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

const float = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(5deg); }
`;

const blink = keyframes`
  0%, 50% { border-color: transparent; }
  51%, 100% { border-color: #40e0ff; }
`;

const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  position: relative;
  overflow: hidden;
`;

const HeroContent = styled.div`
  max-width: 1200px;
  width: 100%;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  z-index: 2;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    text-align: center;
    gap: 3rem;
  }
`;

const ProfileSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const ProfileImageContainer = styled(motion.div)`
  width: 480px;
  height: 480px;
  border-radius: 50%;
  overflow: hidden;
  position: relative;
  margin-bottom: 2rem;
  background: linear-gradient(45deg, 
    rgba(64, 224, 255, 0.3) 0%, 
    rgba(100, 200, 255, 0.2) 50%, 
    rgba(64, 224, 255, 0.3) 100%
  );
  backdrop-filter: blur(10px);
  border: 6px solid rgba(64, 224, 255, 0.3);
  box-shadow: 
    0 20px 40px rgba(0, 0, 0, 0.3),
    inset 0 0 20px rgba(64, 224, 255, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, #40e0ff, #64c8ff, #40e0ff);
    border-radius: 50%;
    z-index: -1;
    animation: ${css`${float} 3s ease-in-out infinite`};
  }

  @media (max-width: 768px) {
    width: 405px;
    height: 405px;
  }
`;

const ProfilePlaceholder = styled.div`
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, 
    rgba(64, 224, 255, 0.2) 0%, 
    rgba(100, 200, 255, 0.1) 50%, 
    rgba(64, 224, 255, 0.2) 100%
  );
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 8rem;
  color: rgba(64, 224, 255, 0.6);
  animation: ${css`${float} 3s ease-in-out infinite`};
`;

const TextSection = styled.div`
  color: white;
  position: relative;
`;

const Greeting = styled(motion.h1)<{ $isTyping?: boolean }>`
  font-size: 1.2rem;
  font-weight: 300;
  color: rgba(64, 224, 255, 0.8);
  margin-bottom: 0.5rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  font-weight: 200;
  overflow: hidden;
  white-space: nowrap;
  display: block;

  &::after {
    content: '';
    display: inline-block;
    width: 2px;
    height: 1.2rem;
    border-right: 2px solid #40e0ff;
    margin-left: 2px;
    animation: ${props => props.$isTyping ? css`${blink} 1s infinite` : 'none'};
  }
`;

const Name = styled(motion.h2)<{ $isTyping?: boolean }>`
  font-size: 4rem;
  font-weight: 100;
  margin-bottom: 1rem;
  background: linear-gradient(45deg, 
    #ffffff 0%, 
    rgba(64, 224, 255, 0.9) 50%, 
    #ffffff 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.1;
  letter-spacing: -2px;
  display: inline-block;
  overflow: hidden;

  &::after {
    content: '';
    display: inline-block;
    width: 3px;
    height: 4rem;
    border-right: 3px solid #40e0ff;
    margin-left: 4px;
    animation: ${props => props.$isTyping ? css`${blink} 1s infinite` : 'none'};
  }

  @media (max-width: 768px) {
    font-size: 3rem;
  }
`;

const Title = styled(motion.h3)`
  font-size: 1.5rem;
  font-weight: 300;
  color: rgba(255, 255, 255, 0.7);
  margin-bottom: 1.5rem;
  line-height: 1.4;
  font-weight: 200;
  min-height: 2.5rem;
  display: flex;
  align-items: center;
`;

const RoleContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  align-items: center;
  min-height: 2.5rem;
`;

const RoleItem = styled.span<{ $isActive?: boolean }>`
  display: inline-block;
  color: ${props => props.$isActive ? '#40e0ff' : 'rgba(255, 255, 255, 0.5)'};
  font-weight: ${props => props.$isActive ? '600' : '300'};
  transition: all 0.5s ease;
  transform: ${props => props.$isActive ? 'scale(1.05)' : 'scale(1)'};
  text-shadow: ${props => props.$isActive ? '0 0 10px rgba(64, 224, 255, 0.3)' : 'none'};

  &:not(:last-child)::after {
    content: ' ‚Ä¢ ';
    color: rgba(255, 255, 255, 0.3);
    margin: 0 0.5rem;
  }
`;

const Bio = styled(motion.p)`
  font-size: 1.1rem;
  line-height: 1.8;
  color: rgba(255, 255, 255, 0.6);
  margin-bottom: 2rem;
  max-width: 500px;
`;

const ContactInfo = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  margin-bottom: 2rem;
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.9rem;
`;

const ContactIcon = styled.span`
  color: #40e0ff;
  font-size: 1rem;
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
`;

const Button = styled(motion.a)`
  padding: 0.75rem 1.5rem;
  border-radius: 50px;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.3s ease;
  cursor: pointer;

  &.primary {
    background: linear-gradient(45deg, #40e0ff, #64c8ff);
    color: #1a1a2e;
    border: none;

    &:hover {
      box-shadow: 0 10px 30px rgba(64, 224, 255, 0.3);
      transform: translateY(-2px);
    }
  }

  &.secondary {
    background: transparent;
    color: #40e0ff;
    border: 2px solid #40e0ff;

    &:hover {
      background: #40e0ff;
      color: #1a1a2e;
      box-shadow: 0 10px 30px rgba(64, 224, 255, 0.3);
    }
  }
`;

const ScrollArrow = styled(motion.div)`
  position: absolute;
  bottom: 2rem;
  left: 50%;
  transform: translateX(-50%);
  color: #40e0ff;
  cursor: pointer;
  font-size: 1.5rem;
  animation: ${css`${float} 2s ease-in-out infinite`};
`;

const Hero: React.FC = () => {
  const { profile } = useData();
  const [showPieChart, setShowPieChart] = useState(true);
  const [greetingText, setGreetingText] = useState('');
  const [nameText, setNameText] = useState('');
  const [currentRoleIndex, setCurrentRoleIndex] = useState(0);
  const [isTyping, setIsTyping] = useState(false);
  const [showName, setShowName] = useState(false);
  const [hasAnimated, setHasAnimated] = useState(false);

  const roles = profile.roles ? profile.roles.split('\n').filter((role: string) => role.trim()) : [];

  // Reset role index when roles change
  useEffect(() => {
    setCurrentRoleIndex(0);
  }, [roles.length]);

  // Typing animation - first line: "Hi, I'm", second line: "Ollie" - RUNS ONLY ONCE
  useEffect(() => {
    if (hasAnimated) return; // Prevent re-running

    const greeting = profile.greeting || "Hi, I'm";
    const name = profile.name || 'Ollie';

    // Start typing after profile animation
    setTimeout(() => {
      console.log('Starting typing animation');
      setHasAnimated(true);
      setIsTyping(true);

      // Type first line: "Hi, I'm"
      let index = 0;
      const typeGreeting = () => {
        if (index < greeting.length) {
          setGreetingText(greeting.slice(0, index + 1));
          setIsTypingGreeting(true);
          setIsTypingName(false);
          console.log('Typing greeting:', greeting.slice(0, index + 1), 'isTypingGreeting:', true, 'isTypingName:', false);
          index++;
          setTimeout(typeGreeting, 100);
        } else {
          // First line complete - stop greeting cursor
          console.log('Greeting complete, stopping greeting cursor');
          setIsTypingGreeting(false);
          setIsTypingName(false);

          // Pause, then start second line
          setTimeout(() => {
            console.log('Starting name line');
            setShowName(true);

            // Type second line: "Ollie"
            let nameIndex = 0;
            const typeName = () => {
              if (nameIndex < name.length) {
                setNameText(name.slice(0, nameIndex + 1));
                setIsTypingName(true);
                setIsTypingGreeting(false);
                console.log('Typing name:', name.slice(0, nameIndex + 1), 'isTypingGreeting:', false, 'isTypingName:', true);
                nameIndex++;
                setTimeout(typeName, 150);
              } else {
                // Second line complete - stop name cursor
                console.log('Name complete, stopping all cursors');
                setTimeout(() => {
                  setIsTypingName(false);
                  setIsTypingGreeting(false);
                  setIsTyping(false);
                  console.log('Animation complete');
                }, 800);
              }
            };

            typeName();
          }, 500);
        }
      };

      typeGreeting();
    }, 1000);
  }, [profile.name, profile.greeting, hasAnimated]);

  // Role rotation effect
  useEffect(() => {
    if (!isTyping && roles.length > 0) {
      const interval = setInterval(() => {
        setCurrentRoleIndex((prev) => (prev + 1) % roles.length);
      }, 3000); // Change role every 3 seconds

      return () => clearInterval(interval);
    }
  }, [isTyping, roles.length]);

  // Switch from analytical animation to profile image
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowPieChart(false);
    }, 3000); // 3 seconds for SQL animation sequence
    return () => clearTimeout(timer);
  }, []);

  return (
    <HeroContainer id="home">
      <HeroContent>
        <ProfileSection>
          <ProfileImageContainer
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 0.8, type: "spring", stiffness: 100 }}
          >
            {showPieChart ? (
              <div style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #1a1a2e 100%)',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                zIndex: 10
              }}>
                {/* Outer spinning ring */}
                <div style={{
                  position: 'absolute',
                  width: '90%',
                  height: '90%',
                  border: '3px solid transparent',
                  borderTop: '3px solid #40e0ff',
                  borderRadius: '50%',
                  animation: 'spin 2s linear infinite'
                }}></div>

                {/* Inner spinning ring */}
                <div style={{
                  position: 'absolute',
                  width: '70%',
                  height: '70%',
                  border: '2px solid transparent',
                  borderBottom: '2px solid #64c8ff',
                  borderRadius: '50%',
                  animation: 'spin 1.5s linear infinite reverse'
                }}></div>

                {/* Center content */}
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  zIndex: 10
                }}>
                  <div style={{
                    width: '50px',
                    height: '50px',
                    border: '3px solid rgba(64, 224, 255, 0.3)',
                    borderTop: '3px solid #40e0ff',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite',
                    marginBottom: '15px'
                  }}></div>

                  <div style={{
                    color: '#40e0ff',
                    fontSize: '13px',
                    fontWeight: '600',
                    textAlign: 'center',
                    marginBottom: '8px',
                    fontFamily: 'Monaco, Consolas, "Courier New", monospace'
                  }}>
                    SELECT * FROM Ollie_gillyon.photographs
                  </div>

                  <div style={{
                    color: '#64c8ff',
                    fontSize: '12px',
                    textAlign: 'center',
                    marginBottom: '6px',
                    opacity: 0.9
                  }}>
                    WHERE photo_type = 'profile'
                  </div>

                  <div style={{
                    color: '#64c8ff',
                    fontSize: '12px',
                    textAlign: 'center',
                    opacity: 0.8
                  }}>
                    AND quality = 'professional'
                  </div>
                </div>

                <style>{`
                  @keyframes spin {
                    0% { transform: rotate(0deg); }
                    100% { transform: rotate(360deg); }
                  }
                `}</style>
              </div>
            ) : (
              <motion.div
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5 }}
                style={{ width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
              >
                {profile.profileImage && profile.profileImage !== '/api/placeholder/300/300' ? (
                  <img
                    src={profile.profileImage}
                    alt="Profile"
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                      borderRadius: '50%'
                    }}
                  />
                ) : (
                  <ProfilePlaceholder>
                    <span>üë®‚Äçüíª</span>
                  </ProfilePlaceholder>
                )}
              </motion.div>
            )}
          </ProfileImageContainer>
        </ProfileSection>

        <TextSection>
          <Greeting
            $isTyping={isTypingGreeting}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            {greetingText}
          </Greeting>

          {showName && (
            <Name
              $isTyping={isTypingName}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              {nameText}
            </Name>
          )}

          {/* Debug info - remove this later */}
          <div style={{ position: 'fixed', top: '10px', right: '10px', background: 'black', color: 'white', padding: '10px', fontSize: '12px', zIndex: 9999 }}>
            <div>isTypingGreeting: {isTypingGreeting.toString()}</div>
            <div>isTypingName: {isTypingName.toString()}</div>
            <div>showName: {showName.toString()}</div>
            <div>hasAnimated: {hasAnimated.toString()}</div>
          </div>

          <Title
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <RoleContainer>
              {roles.length > 0 ? roles.map((role: string, index: number) => (
                <RoleItem
                  key={role}
                  $isActive={index === currentRoleIndex}
                >
                  {role}
                </RoleItem>
              )) : (
                <RoleItem $isActive={true}>
                  {profile.title || 'Professional'}
                </RoleItem>
              )}
            </RoleContainer>
          </Title>

          <Bio
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            {profile.bio || 'Passionate data analyst with expertise in transforming complex data into actionable insights. Specializing in business intelligence, machine learning, and data visualization to drive strategic decision-making.'}
          </Bio>

          <ContactInfo
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            {profile.location && (
              <ContactItem>
                <ContactIcon><FaMapMarkerAlt /></ContactIcon>
                <span>{profile.location}</span>
              </ContactItem>
            )}
            {profile.email && (
              <ContactItem>
                <ContactIcon><FaEnvelope /></ContactIcon>
                <span>{profile.email}</span>
              </ContactItem>
            )}
            {profile.phone && (
              <ContactItem>
                <ContactIcon><FaPhone /></ContactIcon>
                <span>{profile.phone}</span>
              </ContactItem>
            )}
          </ContactInfo>

          <ActionButtons
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1.0 }}
          >
            <Button
              className="primary"
              href="#projects"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              View My Work
            </Button>
            <Button
              className="secondary"
              href="#contact"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Get In Touch
            </Button>
          </ActionButtons>
        </TextSection>
      </HeroContent>

      <ScrollArrow
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2 }}
        onClick={() => {
          const nextSection = document.getElementById('stats');
          nextSection?.scrollIntoView({ behavior: 'smooth' });
        }}
      >
          <FaChevronDown />
        </ScrollArrow>
    </HeroContainer>
  );
};

export default Hero;
