const SkillCategory = styled.span<{ category: string }>`
  background: ${props => {
    switch (props.category) {
      case 'Programming Languages': return '#e3f2fd';
      case 'Data Platforms': return '#f3e5f5';
      case 'Systems & Databases': return '#e8f5e8';
