const SkillLevel = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem;
`;

const LevelBar = styled.div`
  flex: 1;
  height: 8px;
  background: #e9ecef;
  border-radius: 4px;
  overflow: hidden;
`;

const LevelFill = styled.div<{ level: number; category: string }>`
  height: 100%;
  background: ${props => {
    switch (props.category) {
      case 'Programming Languages': return 'linear-gradient(90deg, #1976d2, #42a5f5)';
      case 'Data Platforms': return 'linear-gradient(90deg, #7b1fa2, #ba68c8)';
      case 'Systems & Databases': return 'linear-gradient(90deg, #388e3c, #66bb6a)';
      case 'Analytical Tools': return 'linear-gradient(90deg, #f57c00, #ffb74d)';
      case 'Soft Skills': return 'linear-gradient(90deg, #c2185b, #f06292)';
