const Select = styled.select`
  padding: 0.75rem;
  border: 1px solid #ced4da;
  border-radius: 6px;
  font-size: 1rem;
  transition: border-color 0.3s ease;
  background: white;

  &:focus {
    outline: none;
    border-color: #ffd700;
    box-shadow: 0 0 0 2px rgba(255, 215, 0, 0.1);
  }
`;

const RangeInput = styled.input`
  width: 100%;
  height: 8px;
  border-radius: 4px;
  background: #e9ecef;
  outline: none;
  -webkit-appearance: none;

  &::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: #ffd700;
    cursor: pointer;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
  }

  &::-moz-range-thumb {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: #ffd700;
    cursor: pointer;
    border: none;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
  }
`;

const LevelDisplay = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 0.5rem;
`;

const FormActions = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: flex-end;
`;

const SaveButton = styled(motion.button)`
  background: #28a745;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 6px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    background: #218838;
    transform: translateY(-2px);
  }
`;

const CancelButton = styled(motion.button)`
  background: #6c757d;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 6px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    background: #5a6268;
    transform: translateY(-2px);
  }
`;

const SkillsEditor: React.FC = () => {
  const { skills, addSkill, updateSkill, deleteSkill } = useData();
  const [isAdding, setIsAdding] = useState(false);
  const [editingSkill, setEditingSkill] = useState<string | null>(null);
  const [formData, setFormData] = useState<Partial<Skill>>({
    name: '',
    level: 50,
    category: 'Programming Languages'
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (isAdding) {
      addSkill(formData as Skill);
    } else if (editingSkill) {
      updateSkill(editingSkill, formData as Skill);
    }
    
    handleClose();
  };

  const handleClose = () => {
    setFormData({
      name: '',
      level: 50,
      category: 'Programming Languages'
    });
    setIsAdding(false);
    setEditingSkill(null);
  };

  const handleEdit = (skill: Skill) => {
    setFormData(skill);
    setEditingSkill(skill.name);
    setIsAdding(false);
  };

  const categories = ['Programming Languages', 'Data Platforms', 'Systems & Databases', 'Analytical Tools', 'Soft Skills'];

  // Group skills by category
  const skillsByCategory = skills.reduce((acc, skill) => {
    if (!acc[skill.category]) {
      acc[skill.category] = [];
    }
    acc[skill.category].push(skill);
    return acc;
  }, {} as Record<string, Skill[]>);

  const categoryIcons = {
    'Programming Languages': 'üíª',
    'Data Platforms': '‚òÅÔ∏è',
    'Systems & Databases': 'üóÑÔ∏è',
    'Analytical Tools': 'üìä',
    'Soft Skills': 'ü§ù'
  };

  return (
    <EditorContainer>
      <h2>Manage Skills</h2>

      <ActionButton
        variant="add"
        onClick={() => setIsAdding(true)}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        style={{ marginBottom: '1rem' }}
      >
        <FaPlus />
        Add New Skill
      </ActionButton>

      {(isAdding || editingSkill) && (
        <FormContainer
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
        >
          <Form onSubmit={handleSubmit}>
            <FormRow>
              <FormGroup>
                <Label>Skill Name</Label>
                <Input
                  type="text"
                  value={formData.name || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>Category</Label>
                <Select
                  value={formData.category || 'Technical'}
                  onChange={(e) => setFormData(prev => ({ ...prev, category: e.target.value as Skill['category'] }))}
                >
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </Select>
              </FormGroup>
            </FormRow>

            <FormGroup>
              <Label>Proficiency Level: {formData.level}%</Label>
              <RangeInput
                type="range"
                min="0"
                max="100"
                value={formData.level || 50}
                onChange={(e) => setFormData(prev => ({ ...prev, level: parseInt(e.target.value) }))}
              />
              <LevelDisplay>
                <span>Beginner (0%)</span>
                <span>Expert (100%)</span>
              </LevelDisplay>
            </FormGroup>

            <FormActions>
              <SaveButton
                type="submit"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaSave />
                {isAdding ? 'Add Skill' : 'Update Skill'}
              </SaveButton>
              <CancelButton
                type="button"
                onClick={handleClose}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaTimes />
                Cancel
              </CancelButton>
            </FormActions>
          </Form>
        </FormContainer>
      )}

      <SkillsGrid>
        {skills.map((skill) => (
          <SkillCard
            key={skill.name}
            whileHover={{ scale: 1.02 }}
            transition={{ duration: 0.2 }}
          >
            <SkillHeader>
              <SkillName>{skill.name}</SkillName>
              <SkillCategory>{skill.category}</SkillCategory>
            </SkillHeader>
            
            <SkillLevel>
              <LevelBar>
                <LevelFill level={skill.level} />
              </LevelBar>
              <LevelText>{skill.level}%</LevelText>
            </SkillLevel>
            
            <SkillActions>
              <ActionButton
                variant="edit"
                onClick={() => handleEdit(skill)}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <FaEdit />
              </ActionButton>
              <ActionButton
                variant="delete"
                onClick={() => deleteSkill(skill.name)}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <FaTrash />
              </ActionButton>
            </SkillActions>
          </SkillCard>
        ))}
      </SkillsGrid>
    </EditorContainer>
  );
};

export default SkillsEditor;
