import React, { useState, useEffect } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { motion } from 'framer-motion';
import { FaMapMarkerAlt, FaEnvelope, FaPhone, FaDownload, FaChevronDown, FaLinkedin } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

const float = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(5deg); }
`;

const bounce = keyframes`
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(8px); }
`;

const pulseBorder = keyframes`
  0%, 100% { 
    transform: scale(1) rotate(0deg);
    opacity: 0.6;
  }
  50% { 
    transform: scale(1.02) rotate(180deg);
    opacity: 1;
  }
`;

const blink = keyframes`
  0%, 50% { border-color: transparent; }
  51%, 100% { border-color: #40e0ff; }
`;

const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  position: relative;
  overflow: hidden;
`;

const HeroContent = styled.div`
  max-width: 1200px;
  width: 100%;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  z-index: 2;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    text-align: left;
    gap: 3rem;
  }
`;

const ProfileSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const ProfileImageContainer = styled(motion.div)`
  width: 480px;
  height: 480px;
  border-radius: 50%;
  overflow: hidden;
  position: relative;
  margin-bottom: 2rem;
  background: linear-gradient(45deg, 
    rgba(64, 224, 255, 0.3) 0%, 
    rgba(100, 200, 255, 0.2) 50%, 
    rgba(64, 224, 255, 0.3) 100%
  );
  backdrop-filter: blur(10px);
  border: 6px solid rgba(64, 224, 255, 0.3);
  box-shadow: 
    0 20px 40px rgba(0, 0, 0, 0.3),
    inset 0 0 20px rgba(64, 224, 255, 0.1);
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);

  &:hover {
    transform: scale(1.05) translateY(-5px);
    box-shadow: 
      0 30px 60px rgba(0, 0, 0, 0.4),
      inset 0 0 30px rgba(64, 224, 255, 0.2),
      0 0 40px rgba(64, 224, 255, 0.3);
    border-color: rgba(64, 224, 255, 0.6);
  }

  &::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, #40e0ff, #64c8ff, #40e0ff);
    border-radius: 50%;
    z-index: -1;
    animation: ${css`${pulseBorder} 4s ease-in-out infinite`};
  }

  &::after {
    content: '';
    position: absolute;
    top: -4px;
    left: -4px;
    right: -4px;
    bottom: -4px;
    background: linear-gradient(45deg, 
      rgba(64, 224, 255, 0.8), 
      rgba(100, 200, 255, 0.6), 
      rgba(64, 224, 255, 0.8)
    );
    border-radius: 50%;
    z-index: -2;
    opacity: 0;
    transition: opacity 0.4s ease;
  }

  &:hover::after {
    opacity: 1;
  }

  @media (max-width: 768px) {
    width: 405px;
    height: 405px;
  }
`;

const ProfilePlaceholder = styled.div`
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, 
    rgba(64, 224, 255, 0.2) 0%, 
    rgba(100, 200, 255, 0.1) 50%, 
    rgba(64, 224, 255, 0.2) 100%
  );
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 8rem;
  color: rgba(64, 224, 255, 0.6);
  animation: ${css`${float} 3s ease-in-out infinite`};
`;

const TextSection = styled.div`
  color: white;
  position: relative;
  text-align: left;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
`;

const Greeting = styled(motion.h1)`
  font-size: 1.4rem;
  font-weight: 400;
  color: rgba(64, 224, 255, 0.95);
  margin-bottom: 0.75rem;
  letter-spacing: 2.5px;
  overflow: hidden;
  white-space: nowrap;
  display: block;
  text-align: left;
  text-transform: uppercase;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;

  @media (max-width: 768px) {
    font-size: 1.2rem;
    letter-spacing: 2px;
  }
`;

const Name = styled(motion.h2)`
  font-size: 4.5rem;
  font-weight: 300;
  margin-bottom: 1.25rem;
  background: linear-gradient(135deg, 
    #ffffff 0%, 
    rgba(64, 224, 255, 0.95) 30%,
    rgba(100, 200, 255, 0.8) 70%,
    #ffffff 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.05;
  letter-spacing: -0.5px;
  display: block;
  overflow: hidden;
  text-align: left;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  text-shadow: 0 0 30px rgba(64, 224, 255, 0.3);

  @media (max-width: 768px) {
    font-size: 3.2rem;
    letter-spacing: -0.2px;
  }
`;

const Title = styled(motion.h3)`
  font-size: 1.6rem;
  font-weight: 400;
  color: rgba(255, 255, 255, 0.85);
  margin-bottom: 1.75rem;
  line-height: 1.5;
  min-height: 2.5rem;
  display: flex;
  align-items: flex-start;
  text-align: left;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  letter-spacing: 0.5px;

  @media (max-width: 768px) {
    font-size: 1.4rem;
    line-height: 1.4;
  }
`;

const RoleContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  align-items: center;
  min-height: 2.5rem;
  justify-content: flex-start;
  line-height: 1.4;

  @media (max-width: 768px) {
    gap: 0.25rem;
  }
`;

const RoleItem = styled.span<{ $isActive?: boolean }>`
  display: inline-block;
  color: ${props => props.$isActive ? '#40e0ff' : 'rgba(255, 255, 255, 0.6)'};
  font-weight: ${props => props.$isActive ? '600' : '400'};
  font-size: 1.15rem;
  transition: all 0.5s ease;
  transform: ${props => props.$isActive ? 'scale(1.05)' : 'scale(1)'};
  text-shadow: ${props => props.$isActive ? '0 0 15px rgba(64, 224, 255, 0.4)' : 'none'};
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  letter-spacing: 0.3px;

  &:not(:last-child)::after {
    content: ' ‚Ä¢ ';
    color: rgba(255, 255, 255, 0.4);
    margin: 0 0.5rem;
    font-weight: 300;
  }

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const Bio = styled(motion.p)`
  font-size: 1.3rem;
  line-height: 1.8;
  color: rgba(255, 255, 255, 0.9);
  margin-bottom: 2.5rem;
  text-align: left;
  width: 100%;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-weight: 300;
  letter-spacing: 0.4px;
  max-width: 700px;

  @media (max-width: 768px) {
    font-size: 1.15rem;
    line-height: 1.7;
    letter-spacing: 0.3px;
  }
`;

const ContactInfo = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  margin-bottom: 2rem;
  align-items: flex-start;
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: rgba(255, 255, 255, 0.8);
  font-size: 1rem;
  padding: 0.5rem 0.75rem;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-weight: 400;
  letter-spacing: 0.1px;
  border-radius: 6px;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(64, 224, 255, 0.1);
    color: rgba(255, 255, 255, 0.95);
  }

  @media (max-width: 768px) {
    font-size: 0.9rem;
    padding: 0.4rem 0.6rem;
  }
`;

const ContactLink = styled.a`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: rgba(255, 255, 255, 0.8);
  font-size: 1rem;
  text-decoration: none;
  transition: all 0.3s ease;
  cursor: pointer;
  padding: 0.5rem 0.75rem;
  border-radius: 6px;
  position: relative;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-weight: 400;
  letter-spacing: 0.1px;

  &:hover {
    color: #40e0ff;
    background: rgba(64, 224, 255, 0.1);
    transform: translateX(4px);
  }

  &::after {
    content: '‚Üó';
    opacity: 0;
    transition: opacity 0.3s ease;
    margin-left: 0.25rem;
    font-size: 0.8rem;
    color: #40e0ff;
  }

  &:hover::after {
    opacity: 1;
  }
`;

const ContactIcon = styled.span`
  color: #40e0ff;
  font-size: 1rem;
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
  justify-content: flex-start;
  width: 100%;
`;

const Button = styled(motion.a)`
  padding: 0.75rem 1.5rem;
  border-radius: 50px;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.3s ease;
  cursor: pointer;

  &.primary {
    background: linear-gradient(45deg, #40e0ff, #64c8ff);
    color: #1a1a2e;
    border: none;

    &:hover {
      box-shadow: 0 10px 30px rgba(64, 224, 255, 0.3);
      transform: translateY(-2px);
    }
  }

  &.secondary {
    background: transparent;
    color: #40e0ff;
    border: 2px solid #40e0ff;

    &:hover {
      background: #40e0ff;
      color: #1a1a2e;
      box-shadow: 0 10px 30px rgba(64, 224, 255, 0.3);
    }
  }
`;

const ScrollArrow = styled(motion.div)`
  position: absolute;
  bottom: 2rem;
  left: 50%;
  transform: translateX(-50%);
  color: #40e0ff;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    color: #64c8ff;
    transform: translateX(-50%) translateY(-5px);
  }
`;

const ScrollText = styled.div`
  font-size: 0.9rem;
  font-weight: 300;
  text-transform: uppercase;
  letter-spacing: 2px;
  opacity: 0.8;
`;

const ArrowIcon = styled.div`
  font-size: 1.5rem;
  animation: ${css`${bounce} 2s ease-in-out infinite`};
`;

const Hero: React.FC = () => {
  const { profile } = useData();
  const [showPieChart, setShowPieChart] = useState(true);
  const [greetingText, setGreetingText] = useState('');
  const [nameText, setNameText] = useState('');
  const [currentRoleIndex, setCurrentRoleIndex] = useState(0);
  const [isTyping, setIsTyping] = useState(false);
  const [showName, setShowName] = useState(false);
  const [hasAnimated, setHasAnimated] = useState(false);

  const roles = profile.roles ? profile.roles.split('\n').filter((role: string) => role.trim()) : [];

  // Reset role index when roles change
  useEffect(() => {
    setCurrentRoleIndex(0);
  }, [roles.length]);

  // Simple typing animation - runs only once
  useEffect(() => {
    if (hasAnimated) return;

    const greeting = profile.greeting || "Hi, I'm";
    const name = profile.name || 'Ollie';

    setTimeout(() => {
      setHasAnimated(true);
      setIsTyping(true);

      // Type first line
      let index = 0;
      const typeGreeting = () => {
        if (index < greeting.length) {
          setGreetingText(greeting.slice(0, index + 1));
          index++;
          setTimeout(typeGreeting, 80);
        } else {
          // Pause, then start second line
          setTimeout(() => {
            setShowName(true);

            // Type second line
            let nameIndex = 0;
            const typeName = () => {
              if (nameIndex < name.length) {
                setNameText(name.slice(0, nameIndex + 1));
                nameIndex++;
                setTimeout(typeName, 120);
              } else {
                // Animation complete
                setTimeout(() => {
                  setIsTyping(false);
                }, 600);
              }
            };

            typeName();
          }, 400);
        }
      };

      typeGreeting();
    }, 1000);
  }, [profile.name, profile.greeting, hasAnimated]);

  // Role rotation effect
  useEffect(() => {
    if (!isTyping && roles.length > 0) {
      const interval = setInterval(() => {
        setCurrentRoleIndex((prev) => (prev + 1) % roles.length);
      }, 3000); // Change role every 3 seconds

      return () => clearInterval(interval);
    }
  }, [isTyping, roles.length]);

  // Switch from analytical animation to profile image
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowPieChart(false);
    }, 3000); // 3 seconds for SQL animation sequence
    return () => clearTimeout(timer);
  }, []);

  return (
    <HeroContainer id="home">
      <HeroContent>
        <ProfileSection>
          <ProfileImageContainer
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 0.8, type: "spring", stiffness: 100 }}
          >
            {showPieChart ? (
              <div style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #1a1a2e 100%)',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                zIndex: 10
              }}>
                {/* Outer spinning ring */}
                <div style={{
                  position: 'absolute',
                  width: '90%',
                  height: '90%',
                  border: '3px solid transparent',
                  borderTop: '3px solid #40e0ff',
                  borderRadius: '50%',
                  animation: 'spin 2s linear infinite'
                }}></div>

                {/* Inner spinning ring */}
                <div style={{
                  position: 'absolute',
                  width: '70%',
                  height: '70%',
                  border: '2px solid transparent',
                  borderBottom: '2px solid #64c8ff',
                  borderRadius: '50%',
                  animation: 'spin 1.5s linear infinite reverse'
                }}></div>

                {/* Center content */}
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  zIndex: 10,
                  width: '100%',
                  padding: '20px'
                }}>
                  <div style={{
                    color: '#40e0ff',
                    fontSize: '13px',
                    fontWeight: '600',
                    textAlign: 'center',
                    marginBottom: '8px',
                    fontFamily: 'Monaco, Consolas, "Courier New", monospace'
                  }}>
                    SELECT * FROM Ollie.photograph
                  </div>

                  <div style={{
                    color: '#64c8ff',
                    fontSize: '12px',
                    textAlign: 'center',
                    marginBottom: '6px',
                    opacity: 0.9
                  }}>
                    WHERE photo_type = 'profile'
                  </div>

                  <div style={{
                    color: '#64c8ff',
                    fontSize: '12px',
                    textAlign: 'center',
                    marginBottom: '15px',
                    opacity: 0.8
                  }}>
                    AND quality = 'professional'
                  </div>

                  {/* Status Text */}
                  <div style={{
                    color: '#64c8ff',
                    fontSize: '11px',
                    textAlign: 'center',
                    opacity: 0.7,
                    fontFamily: 'Monaco, Consolas, "Courier New", monospace'
                  }}>
                    Executing query...
                  </div>
                </div>

                <style>{`
                  @keyframes spin {
                    0% { transform: rotate(0deg); }
                    100% { transform: rotate(360deg); }
                  }
                `}</style>
              </div>
            ) : (
              <motion.div
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5 }}
                style={{ width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
              >
                {profile.profileImage && profile.profileImage !== '/api/placeholder/300/300' ? (
                  <img
                    src={profile.profileImage}
                    alt="Profile"
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                      borderRadius: '50%'
                    }}
                  />
                ) : (
                  <ProfilePlaceholder>
                    <span>üë®‚Äçüíª</span>
                  </ProfilePlaceholder>
                )}
              </motion.div>
            )}
          </ProfileImageContainer>
        </ProfileSection>

        <TextSection>
          <Greeting
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            {greetingText}
          </Greeting>

          {showName && (
            <Name
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              {nameText}
            </Name>
          )}

          <Title
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <RoleContainer>
              {roles.length > 0 ? roles.map((role: string, index: number) => (
                <RoleItem
                  key={role}
                  $isActive={index === currentRoleIndex}
                >
                  {role}
                </RoleItem>
              )) : (
                <RoleItem $isActive={true}>
                  {profile.title || 'Professional'}
                </RoleItem>
              )}
            </RoleContainer>
          </Title>

          <Bio
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            {profile.bio || 'Passionate data analyst with expertise in transforming complex data into actionable insights. Specializing in business intelligence, machine learning, and data visualization to drive strategic decision-making.'}
          </Bio>

          <ContactInfo
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            {profile.location && (
              <ContactItem>
                <ContactIcon><FaMapMarkerAlt /></ContactIcon>
                <span>{profile.location}</span>
              </ContactItem>
            )}
            {profile.email && (
              <ContactLink href={`mailto:${profile.email}`}>
                <ContactIcon><FaEnvelope /></ContactIcon>
                <span>{profile.email}</span>
              </ContactLink>
            )}
            <ContactLink href={profile.linkedin || "https://linkedin.com/in/ollie-gillyon"} target="_blank" rel="noopener noreferrer">
              <ContactIcon><FaLinkedin /></ContactIcon>
              <span>LinkedIn Profile</span>
            </ContactLink>
            {profile.phone && (
              <ContactItem>
                <ContactIcon><FaPhone /></ContactIcon>
                <span>{profile.phone}</span>
              </ContactItem>
            )}
          </ContactInfo>

        </TextSection>
      </HeroContent>

      <ScrollArrow
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2 }}
        onClick={() => {
          const nextSection = document.getElementById('stats');
          nextSection?.scrollIntoView({ behavior: 'smooth' });
        }}
      >
        <ScrollText>Explore More</ScrollText>
        <ArrowIcon>
          <FaChevronDown />
        </ArrowIcon>
      </ScrollArrow>
    </HeroContainer>
  );
};

export default Hero;
