import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaMapMarkerAlt, FaEnvelope, FaPhone, FaDownload, FaChevronDown } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

const liquidFlow = keyframes`
  0% { transform: translateX(-100%) rotate(0deg); }
  100% { transform: translateX(100vw) rotate(360deg); }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(5deg); }
`;


const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  position: relative;
  overflow: hidden;
`;

const HeroContent = styled.div`
  max-width: 1200px;
  width: 100%;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  z-index: 2;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    text-align: center;
    gap: 3rem;
  }
`;

const ProfileSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const ProfileImageContainer = styled(motion.div)`
  width: 300px;
  height: 300px;
  border-radius: 50%;
  overflow: hidden;
  position: relative;
  margin-bottom: 2rem;
  background: linear-gradient(45deg, 
    rgba(64, 224, 255, 0.3) 0%, 
    rgba(100, 200, 255, 0.2) 50%, 
    rgba(64, 224, 255, 0.3) 100%
  );
  backdrop-filter: blur(10px);
  border: 2px solid rgba(64, 224, 255, 0.3);
  box-shadow: 
    0 20px 40px rgba(0, 0, 0, 0.3),
    inset 0 0 20px rgba(64, 224, 255, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, 
      rgba(64, 224, 255, 0.5), 
      rgba(100, 200, 255, 0.3), 
      rgba(64, 224, 255, 0.5)
    );
    border-radius: 50%;
    z-index: -1;
    animation: ${liquidFlow} 8s linear infinite;
  }

  @media (max-width: 768px) {
    width: 250px;
    height: 250px;
  }
`;

const ProfileImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 50%;
  position: relative;
  z-index: 2;
`;

const PieChartContainer = styled(motion.div)`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  z-index: 1;
`;

const PieSlice = styled(motion.div)<{ 
  startAngle: number; 
  endAngle: number; 
  color: string;
  delay: number;
}>`
  position: absolute;
  top: 50%;
  left: 50%;
  width: 50%;
  height: 50%;
  transform-origin: 0 0;
  background: ${props => props.color};
  clip-path: polygon(0 0, 100% 0, 50% 50%);
  transform: rotate(${props => props.startAngle}deg);
`;

const PieCenter = styled(motion.div)`
