const Hero: React.FC = () => {
  const { profile } = useData();
  const [showPieChart, setShowPieChart] = useState(true);
  const [greetingText, setGreetingText] = useState('');
  const [nameText, setNameText] = useState('');
  const [currentRoleIndex, setCurrentRoleIndex] = useState(0);
  const [isTyping, setIsTyping] = useState(true);

  const roles = [
    'Data Analyst',
    'BI Developer', 
    'Process Optimisation Expert',
    'Machine Learning Enthusiast',
    'Data Visualization Specialist'
  ];

  // Typing animation for greeting
  useEffect(() => {
    const greeting = "Hi, I'm";
    let index = 0;

    const typeGreeting = () => {
      if (index < greeting.length) {
        setGreetingText(greeting.slice(0, index + 1));
        index++;
        setTimeout(typeGreeting, 100);
      } else {
        // Start typing name after greeting is complete
        setTimeout(() => {
          const name = profile.name || 'Ollie';
          let nameIndex = 0;

          const typeName = () => {
            if (nameIndex < name.length) {
              setNameText(name.slice(0, nameIndex + 1));
              nameIndex++;
              setTimeout(typeName, 150);
            } else {
              setIsTyping(false);
            }
          };

          typeName();
        }, 500);
      }
    };

    setTimeout(typeGreeting, 1000); // Start after profile animation
  }, [profile.name]);

  // Role rotation effect
  useEffect(() => {
    if (!isTyping) {
      const interval = setInterval(() => {
        setCurrentRoleIndex((prev) => (prev + 1) % roles.length);
      }, 3000); // Change role every 3 seconds

      return () => clearInterval(interval);
    }
  }, [isTyping, roles.length]);

  // Switch from analytical animation to profile image
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowPieChart(false);
    }, 2000); // 2 seconds for analytical animation sequence
    return () => clearTimeout(timer);
  }, []);

  return (
    <HeroContainer id="home">
      <HeroContent>
        <ProfileSection>
          <ProfileImageContainer
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 0.8, type: "spring", stiffness: 100 }}
          >
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
              style={{ width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
            >
              <ProfilePlaceholder>
                <span>üë®‚Äçüíª</span>
              </ProfilePlaceholder>
            </motion.div>
          </ProfileImageContainer>
        </ProfileSection>

        <TextSection>
          <Greeting
            $isTyping={isTyping}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            {greetingText || "Hi, I'm"}
          </Greeting>

          <Name
            $isTyping={isTyping}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            {nameText || profile.name || 'Ollie'}
          </Name>

          <Title
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <RoleContainer>
              {roles.map((role, index) => (
                <RoleItem
                  key={role}
                  $isActive={index === currentRoleIndex}
                >
                  {role}
                </RoleItem>
              ))}
            </RoleContainer>
          </Title>

          <Bio
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            {profile.bio || 'Passionate data analyst with expertise in transforming complex data into actionable insights. Specializing in business intelligence, machine learning, and data visualization to drive strategic decision-making.'}
          </Bio>

          <ContactInfo
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            {profile.location && (
              <ContactItem>
                <ContactIcon><FaMapMarkerAlt /></ContactIcon>
                <span>{profile.location}</span>
              </ContactItem>
            )}
            {profile.email && (
              <ContactItem>
                <ContactIcon><FaEnvelope /></ContactIcon>
                <span>{profile.email}</span>
              </ContactItem>
            )}
            {profile.phone && (
              <ContactItem>
                <ContactIcon><FaPhone /></ContactIcon>
                <span>{profile.phone}</span>
              </ContactItem>
            )}
          </ContactInfo>

          <ActionButtons
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1.0 }}
          >
            <Button
              className="primary"
              href="#projects"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              View My Work
            </Button>
            <Button
              className="secondary"
              href="#contact"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Get In Touch
            </Button>
          </ActionButtons>
        </TextSection>
      </HeroContent>

      <ScrollArrow
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2 }}
        onClick={() => {
          const nextSection = document.getElementById('stats');
          nextSection?.scrollIntoView({ behavior: 'smooth' });
        }}
      >
        <FaChevronDown />
      </ScrollArrow>
    </HeroContainer>
  );
};

