import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';
import { FaBriefcase, FaCalendarAlt, FaMapMarkerAlt } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

const ExperienceContainer = styled.section`
  padding: 6rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: 4rem;
  position: relative;
  perspective: 1000px;
`;

const FloatingContainer = styled(motion.div)`
  position: relative;
  display: inline-block;
  transform-style: preserve-3d;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 3.5rem;
  font-weight: 200;
  margin-bottom: 2rem;
  background: linear-gradient(135deg, 
    #ffffff 0%, 
    rgba(64, 224, 255, 0.95) 30%,
    rgba(100, 200, 255, 0.8) 70%,
    #ffffff 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.1;
  letter-spacing: -1px;
  position: relative;
  text-shadow: 0 0 30px rgba(64, 224, 255, 0.3);
  filter: drop-shadow(0 0 20px rgba(64, 224, 255, 0.2));
`;

const ParticleContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
  overflow: hidden;
`;

const Particle = styled(motion.div)`
  position: absolute;
  width: 4px;
  height: 4px;
  background: radial-gradient(circle, rgba(64, 224, 255, 0.8) 0%, transparent 70%);
  border-radius: 50%;
  box-shadow: 0 0 10px rgba(64, 224, 255, 0.6);
`;

const GlowEffect = styled(motion.div)`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 200px;
  height: 200px;
  background: radial-gradient(circle, rgba(64, 224, 255, 0.1) 0%, transparent 70%);
  border-radius: 50%;
  filter: blur(40px);
  z-index: -1;
`;

const SectionSubtitle = styled(motion.p)`
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.8);
  font-weight: 300;
  letter-spacing: 1px;
  line-height: 1.6;
  max-width: 600px;
  margin: 0 auto;
  text-transform: uppercase;
  font-size: 0.9rem;
  opacity: 0.7;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: -1rem;
    left: 50%;
    transform: translateX(-50%);
    width: 100px;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(64, 224, 255, 0.5), transparent);
  }
`;

const Timeline = styled.div`
  max-width: 800px;
  margin: 0 auto;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 2px;
    background: linear-gradient(to bottom, #ffd700, #ffed4e);
    transform: translateX(-50%);

    @media (max-width: 768px) {
      left: 20px;
    }
  }
`;

const TimelineItem = styled(motion.div)<{ isLeft: boolean }>`
  position: relative;
  margin-bottom: 3rem;
  width: 50%;

  ${props => props.isLeft ? `
    left: 0;
    padding-right: 3rem;
    text-align: right;

    @media (max-width: 768px) {
      left: 0;
      width: 100%;
      padding-right: 0;
      padding-left: 3rem;
      text-align: left;
    }
  ` : `
    left: 50%;
    padding-left: 3rem;

    @media (max-width: 768px) {
      left: 0;
      width: 100%;
      padding-left: 3rem;
    }
  `}
`;

const TimelineDot = styled.div<{ isLeft: boolean }>`
  position: absolute;
  top: 1rem;
  width: 20px;
  height: 20px;
  background: #ffd700;
  border-radius: 50%;
  border: 4px solid white;
  box-shadow: 0 0 0 4px rgba(255, 215, 0, 0.3);

  ${props => props.isLeft ? `
    right: -10px;

    @media (max-width: 768px) {
      left: 10px;
      right: auto;
    }
  ` : `
    left: -10px;

    @media (max-width: 768px) {
      left: 10px;
    }
  `}
`;

const ExperienceCard = styled.div`
  background: rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.3);
  backdrop-filter: blur(10px);
  position: relative;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
    border-color: #ffd700;
  }
`;

const JobTitle = styled.h3`
  font-size: 1.4rem;
  font-weight: 600;
  color: white;
  margin-bottom: 0.5rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const CompanyName = styled.h4`
  font-size: 1.1rem;
  color: #ffd700;
  margin-bottom: 0.5rem;
  font-weight: 500;
`;

const JobDuration = styled.div`
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.9rem;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const JobDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
  margin-bottom: 1rem;
`;

const AchievementsList = styled.ul`
  list-style: none;
  padding: 0;
  margin-bottom: 1rem;
`;

const AchievementItem = styled.li`
  color: rgba(255, 255, 255, 0.8);
  padding: 0.25rem 0;
  position: relative;
  padding-left: 1.5rem;

  &::before {
    content: 'â–¶';
    position: absolute;
    left: 0;
    color: #ffd700;
    font-size: 0.8rem;
  }
`;

const TechnologiesList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const TechnologyTag = styled.span`
  background: rgba(255, 215, 0, 0.2);
  color: #ffd700;
  padding: 0.25rem 0.75rem;
  border-radius: 15px;
  font-size: 0.8rem;
  font-weight: 500;
  border: 1px solid rgba(255, 215, 0, 0.3);
`;

const Experience: React.FC = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });
  const { experiences } = useData();

  return (
    <ExperienceContainer id="experience" ref={ref}>
      <SectionHeader
        initial={{ opacity: 0, y: 30 }}
        animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.6 }}
      >
        <SectionTitle>
          How I got here...
        </SectionTitle>

        <SectionSubtitle>
          How it started â†’ how it's going
        </SectionSubtitle>
      </SectionHeader>

      <Timeline>
        {experiences.map((experience, index) => (
          <TimelineItem
            key={experience.id}
            isLeft={index % 2 === 0}
            initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
            animate={isInView ? { opacity: 1, x: 0 } : { opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
            transition={{ duration: 0.6, delay: index * 0.2 }}
          >
            <TimelineDot isLeft={index % 2 === 0} />
            <ExperienceCard
              as={motion.div}
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              <JobTitle>
                <FaBriefcase />
                {experience.title}
              </JobTitle>
              <CompanyName>{experience.company}</CompanyName>
              <JobDuration>
                <FaCalendarAlt />
                {experience.duration}
                <FaMapMarkerAlt style={{ marginLeft: '1rem' }} />
                {experience.location}
              </JobDuration>

              <JobDescription>{experience.description}</JobDescription>

              <AchievementsList>
                {experience.achievements.map((achievement, achIndex) => (
                  <AchievementItem key={achIndex}>
                    {achievement}
                  </AchievementItem>
                ))}
              </AchievementsList>

              <TechnologiesList>
                {experience.technologies.map((tech, techIndex) => (
                  <TechnologyTag key={techIndex}>
                    {tech}
                  </TechnologyTag>
                ))}
              </TechnologiesList>
            </ExperienceCard>
          </TimelineItem>
        ))}
      </Timeline>
    </ExperienceContainer>
  );
};

export default Experience;
