import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';
import { FaBriefcase, FaCalendarAlt, FaMapMarkerAlt } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

const ExperienceContainer = styled.section`
  padding: 6rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: 4rem;
  position: relative;
`;

const TypographyContainer = styled(motion.div)`
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.2rem;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 4rem;
  font-weight: 100;
  background: linear-gradient(135deg, 
    #ffffff 0%, 
    rgba(64, 224, 255, 0.95) 30%,
    rgba(100, 200, 255, 0.8) 70%,
    #ffffff 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.1;
  letter-spacing: -2px;
  position: relative;
  text-shadow: 0 0 30px rgba(64, 224, 255, 0.3);
  filter: drop-shadow(0 0 20px rgba(64, 224, 255, 0.2));
  margin: 0;
`;

const TitleAccent = styled(motion.div)`
  width: 200px;
  height: 1px;
  background: linear-gradient(90deg, 
    transparent 0%, 
    rgba(64, 224, 255, 0.3) 20%,
    rgba(64, 224, 255, 0.8) 50%,
    rgba(64, 224, 255, 0.3) 80%,
    transparent 100%
  );
  margin: 0.5rem 0;
  border-radius: 1px;
`;

const SectionSubtitle = styled(motion.p)`
  font-size: 0.9rem;
  color: rgba(64, 224, 255, 0.8);
  font-weight: 300;
  letter-spacing: 4px;
  line-height: 1.4;
  text-transform: uppercase;
  position: relative;
  margin: 0;
  text-shadow: 0 0 10px rgba(64, 224, 255, 0.2);
  overflow: hidden;


  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: -30px;
    width: 60px;
    height: 60px;
    background: radial-gradient(circle, 
      rgba(64, 224, 255, 0.8) 0%,
      rgba(64, 224, 255, 0.4) 40%,
      rgba(64, 224, 255, 0.1) 70%,
      transparent 100%
    );
    border-radius: 50%;
    transform: translateY(-50%);
    animation: rippleEffect 6s linear infinite;
    z-index: 0;
    box-shadow: 0 0 20px rgba(64, 224, 255, 0.6);
  }


  @keyframes rippleEffect {
    0% { 
      left: -30px; 
      transform: translateY(-50%) scale(0.3);
      opacity: 0;
    }
    5% { 
      transform: translateY(-50%) scale(1.2);
      opacity: 1;
    }
    15% { 
      transform: translateY(-50%) scale(0.8);
      opacity: 0.8;
    }
    85% { 
      transform: translateY(-50%) scale(0.8);
      opacity: 0.8;
    }
    95% { 
      transform: translateY(-50%) scale(1.2);
      opacity: 1;
    }
    100% { 
      left: calc(100% + 30px);
      transform: translateY(-50%) scale(0.3);
      opacity: 0;
    }
  }
`;

const ParticleTrail = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
  overflow: hidden;
  z-index: 0;
`;

const Particle = styled(motion.div)`
  position: absolute;
  width: 4px;
  height: 4px;
  background: radial-gradient(circle, rgba(64, 224, 255, 1) 0%, rgba(64, 224, 255, 0.6) 50%, transparent 100%);
  border-radius: 50%;
  box-shadow: 0 0 12px rgba(64, 224, 255, 0.8), 0 0 24px rgba(64, 224, 255, 0.4);
`;

const SubtitleAccent = styled(motion.div)`
  width: 100px;
  height: 1px;
  background: linear-gradient(90deg, 
    transparent 0%, 
    rgba(64, 224, 255, 0.4) 50%,
    transparent 100%
  );
  margin: 0.2rem 0;
  border-radius: 1px;
`;

const Timeline = styled.div`
  max-width: 800px;
  margin: 0 auto;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 2px;
    background: linear-gradient(to bottom, #ffd700, #ffed4e);
    transform: translateX(-50%);

    @media (max-width: 768px) {
      left: 20px;
    }
  }
`;

const TimelineItem = styled(motion.div)<{ isLeft: boolean }>`
  position: relative;
  margin-bottom: 3rem;
  width: 50%;

  ${props => props.isLeft ? `
    left: 0;
    padding-right: 3rem;
    text-align: right;

    @media (max-width: 768px) {
      left: 0;
      width: 100%;
      padding-right: 0;
      padding-left: 3rem;
      text-align: left;
    }
  ` : `
    left: 50%;
    padding-left: 3rem;

    @media (max-width: 768px) {
      left: 0;
      width: 100%;
      padding-left: 3rem;
    }
  `}
`;

const TimelineDot = styled.div<{ isLeft: boolean }>`
  position: absolute;
  top: 1rem;
  width: 20px;
  height: 20px;
  background: #ffd700;
  border-radius: 50%;
  border: 4px solid white;
  box-shadow: 0 0 0 4px rgba(255, 215, 0, 0.3);

  ${props => props.isLeft ? `
    right: -10px;

    @media (max-width: 768px) {
      left: 10px;
      right: auto;
    }
  ` : `
    left: -10px;

    @media (max-width: 768px) {
      left: 10px;
    }
  `}
`;

const ExperienceCard = styled.div`
  background: rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  padding: 2rem;
