const Hero: React.FC = () => {
  const { profile } = useData();

  // Safe enhancement context with fallback
  let enhancements;
  try {
    const enhancementContext = useEnhancements();
    enhancements = enhancementContext.enhancements;
  } catch (error) {
    console.warn('Enhancement context not available, using defaults');
    enhancements = null;
  }

  // Fallback if enhancements context fails
  const safeEnhancements = enhancements || {
    profileHover: true,
    roleClickable: true,
    textShadows: true,
    microAnimations: true,
    backgroundPatterns: true,
    enhancedScrollPrompt: true,
    tagline: true,
    enhancedBio: true
  };
  const [showPieChart, setShowPieChart] = useState(true);
  const [greetingText, setGreetingText] = useState('');
  const [nameText, setNameText] = useState('');
  const [currentRoleIndex, setCurrentRoleIndex] = useState(0);
  const [isTyping, setIsTyping] = useState(false);
  const [showName, setShowName] = useState(false);
  const [hasAnimated, setHasAnimated] = useState(false);

  // Role descriptions for hover
  const roleDescriptions = {
    'Data Analyst': 'Transforming raw data into actionable business insights',
    'BI Developer': 'Building powerful dashboards and reporting solutions',
    'Process Optimisation Expert': 'Streamlining workflows for maximum efficiency',
    'Machine Learning Enthusiast': 'Exploring AI and predictive analytics',
    'Data Visualization Specialist': 'Creating compelling visual stories from data'
  } as Record<string, string>;

  const roles = profile.roles ? profile.roles.split('\n').filter((role: string) => role.trim()) : [];

  // Reset role index when roles change
  useEffect(() => {
    setCurrentRoleIndex(0);
  }, [roles.length]);

  // Simple typing animation - runs only once
  useEffect(() => {
    if (hasAnimated) return;

    const greeting = profile.greeting || "Hi, I'm";
    const name = profile.name || 'Ollie';

    setTimeout(() => {
      setHasAnimated(true);
      setIsTyping(true);

      // Type first line
      let index = 0;
      const typeGreeting = () => {
        if (index < greeting.length) {
          setGreetingText(greeting.slice(0, index + 1));
          index++;
          setTimeout(typeGreeting, 80);
        } else {
          // Pause, then start second line
          setTimeout(() => {
            setShowName(true);

            // Type second line
            let nameIndex = 0;
            const typeName = () => {
              if (nameIndex < name.length) {
                setNameText(name.slice(0, nameIndex + 1));
                nameIndex++;
                setTimeout(typeName, 120);
              } else {
                // Animation complete
                setTimeout(() => {
                  setIsTyping(false);
                }, 600);
              }
            };

            typeName();
          }, 400);
        }
      };

      typeGreeting();
    }, 1000);
  }, [profile.name, profile.greeting, hasAnimated]);

  // Role rotation effect
  useEffect(() => {
    if (!isTyping && roles.length > 0) {
      const interval = setInterval(() => {
        setCurrentRoleIndex((prev) => (prev + 1) % roles.length);
      }, 3000); // Change role every 3 seconds

      return () => clearInterval(interval);
    }
  }, [isTyping, roles.length]);

  // Switch from analytical animation to profile image
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowPieChart(false);
    }, 3000); // 3 seconds for SQL animation sequence
    return () => clearTimeout(timer);
  }, []);

  return (
    <HeroContainer id="home">
      <BackgroundPattern $enhancements={safeEnhancements} />
      <HeroContent>
        <ProfileSection>
          <ProfileImageContainer
            $enhancements={safeEnhancements}
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 0.8, type: "spring", stiffness: 100 }}
          >
            {showPieChart ? (
              <div style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #1a1a2e 100%)',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                zIndex: 10
              }}>
                {/* Outer spinning ring */}
                <div style={{
                  position: 'absolute',
                  width: '90%',
                  height: '90%',
                  border: '3px solid transparent',
                  borderTop: '3px solid #40e0ff',
                  borderRadius: '50%',
                  animation: 'spin 2s linear infinite'
                }}></div>

                {/* Inner spinning ring */}
                <div style={{
                  position: 'absolute',
                  width: '70%',
                  height: '70%',
                  border: '2px solid transparent',
                  borderBottom: '2px solid #64c8ff',
                  borderRadius: '50%',
                  animation: 'spin 1.5s linear infinite reverse'
                }}></div>

                {/* Center content */}
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  zIndex: 10
                }}>
                  <div style={{
                    width: '50px',
                    height: '50px',
                    border: '3px solid rgba(64, 224, 255, 0.3)',
                    borderTop: '3px solid #40e0ff',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite',
                    marginBottom: '15px'
                  }}></div>

                  <div style={{
                    color: '#40e0ff',
                    fontSize: '13px',
                    fontWeight: '600',
                    textAlign: 'center',
                    marginBottom: '8px',
                    fontFamily: 'Monaco, Consolas, "Courier New", monospace'
                  }}>
                    SELECT * FROM Ollie.photograph
                  </div>

                  <div style={{
                    color: '#64c8ff',
                    fontSize: '12px',
                    textAlign: 'center',
                    marginBottom: '6px',
                    opacity: 0.9
                  }}>
                    WHERE photo_type = 'profile'
                  </div>

                  <div style={{
                    color: '#64c8ff',
                    fontSize: '12px',
                    textAlign: 'center',
                    opacity: 0.8
                  }}>
                    AND quality = 'professional'
                  </div>
                </div>

                <style>{`
                  @keyframes spin {
                    0% { transform: rotate(0deg); }
                    100% { transform: rotate(360deg); }
                  }
                `}</style>
              </div>
            ) : (
              <motion.div
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5 }}
                style={{ width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
              >
                {profile.profileImage && profile.profileImage !== '/api/placeholder/300/300' ? (
                  <img
                    src={profile.profileImage}
                    alt="Profile"
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                      borderRadius: '50%'
                    }}
                  />
                ) : (
                  <ProfilePlaceholder>
                    <span>👨‍💻</span>
                  </ProfilePlaceholder>
                )}
              </motion.div>
            )}
          </ProfileImageContainer>
        </ProfileSection>

        <TextSection>
          <Greeting
            $enhancements={safeEnhancements}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            {greetingText}
          </Greeting>

          {showName && (
            <Name
              $enhancements={safeEnhancements}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              {nameText}
            </Name>
          )}

          {safeEnhancements.tagline && (
            <Tagline
              $enhancements={safeEnhancements}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              Transforming data into insights
            </Tagline>
          )}

          <Title
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <RoleContainer>
              {roles.length > 0 ? roles.map((role: string, index: number) => (
                <RoleItem
                  key={role}
                  $isActive={index === currentRoleIndex}
                  $enhancements={safeEnhancements}
                  onClick={() => safeEnhancements.roleClickable && console.log(roleDescriptions[role] || 'No description available')}
                  title={safeEnhancements.roleClickable ? (roleDescriptions[role] || 'Click for more info') : undefined}
                >
                  {role}
                </RoleItem>
              )) : (
                <RoleItem $isActive={true} $enhancements={safeEnhancements}>
                  {profile.title || 'Professional'}
                </RoleItem>
              )}
            </RoleContainer>
          </Title>

          <Bio
            $enhancements={safeEnhancements}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            {profile.bio || 'Passionate data analyst with expertise in transforming complex data into actionable insights. Specializing in business intelligence, machine learning, and data visualization to drive strategic decision-making.'}
          </Bio>

          <ContactInfo
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            {profile.location && (
              <ContactItem>
                <ContactIcon><FaMapMarkerAlt /></ContactIcon>
                <span>{profile.location}</span>
              </ContactItem>
            )}
            {profile.email && (
              <ContactLink href={`mailto:${profile.email}`}>
                <ContactIcon><FaEnvelope /></ContactIcon>
                <span>{profile.email}</span>
              </ContactLink>
            )}
            <ContactLink href={profile.linkedin || "https://linkedin.com/in/ollie-gillyon"} target="_blank" rel="noopener noreferrer">
              <ContactIcon><FaLinkedin /></ContactIcon>
              <span>LinkedIn Profile</span>
            </ContactLink>
            {profile.phone && (
              <ContactItem>
                <ContactIcon><FaPhone /></ContactIcon>
                <span>{profile.phone}</span>
              </ContactItem>
            )}
          </ContactInfo>

        </TextSection>
      </HeroContent>

      <ScrollArrow
        $enhancements={enhancements}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2 }}
        onClick={() => {
          const nextSection = document.getElementById('stats');
          nextSection?.scrollIntoView({ behavior: 'smooth' });
        }}
      >
        <ScrollText>Explore More</ScrollText>
        <ArrowIcon>
          <FaChevronDown />
        </ArrowIcon>
      </ScrollArrow>
    </HeroContainer>
  );
};

export default Hero;
