const ProfileEditor: React.FC = () => {
  const { profile, updateProfile } = useData();
  const [formData, setFormData] = useState({
    name: '',
    title: '',
    location: '',
    email: '',
    phone: '',
    profileImage: '',
    bio: ''
  });
  const [isDragOver, setIsDragOver] = useState(false);
  const [showCropper, setShowCropper] = useState(false);
  const [imageToCrop, setImageToCrop] = useState<string>('');
  const [isSaving, setIsSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState<string>('');

  useEffect(() => {
    setFormData(profile);
  }, [profile]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateProfile(formData);
  };

  const handleChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Check if file is an image
      if (!file.type.startsWith('image/')) {
        alert('Please select an image file');
        return;
      }

      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert('File size must be less than 5MB');
        return;
      }

      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        setImageToCrop(result);
        setShowCropper(true);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleRemoveImage = () => {
    setFormData(prev => ({ ...prev, profileImage: '' }));
  };

  const handleCropComplete = (croppedImageUrl: string) => {
    setFormData(prev => ({ ...prev, profileImage: croppedImageUrl }));
    setShowCropper(false);
    setImageToCrop('');
  };

  const handleCropCancel = () => {
    setShowCropper(false);
    setImageToCrop('');
  };

  const handleCropExisting = () => {
    if (formData.profileImage) {
      setImageToCrop(formData.profileImage);
      setShowCropper(true);
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type.startsWith('image/')) {
        if (file.size > 5 * 1024 * 1024) {
          alert('File size must be less than 5MB');
          return;
        }

        const reader = new FileReader();
        reader.onload = (e) => {
          const result = e.target?.result as string;
          setImageToCrop(result);
          setShowCropper(true);
        };
        reader.readAsDataURL(file);
      } else {
        alert('Please drop an image file');
      }
    }
  };

  return (
    <EditorContainer>
      <h2>
        <FaUser />
        Profile Settings
      </h2>

      <FormContainer
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
      >
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label>Profile Image</Label>
            <UploadSection>
              <DropZone
                isDragOver={isDragOver}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
                onClick={() => document.getElementById('profileImage')?.click()}
              >
                <DropText>
                  <FaUpload style={{ fontSize: '2rem', marginBottom: '0.5rem' }} />
                </DropText>
                <DropText>Drag & drop an image here</DropText>
                <DropSubtext>or click to browse files</DropSubtext>
              </DropZone>

              <FileInput
                type="file"
                id="profileImage"
                accept="image/*"
                onChange={handleFileUpload}
              />

              <Input
                type="url"
                value={formData.profileImage}
                onChange={(e) => handleChange('profileImage', e.target.value)}
                placeholder="Or paste image URL here..."
              />
            </UploadSection>
            <ImagePreview>
              {formData.profileImage ? (
                <>
                  <PreviewImage src={formData.profileImage} alt="Profile Preview" />
                  <ImageActions>
                    <RemoveButton
                      type="button"
                      onClick={handleCropExisting}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      style={{ background: 'linear-gradient(45deg, #667eea, #764ba2)' }}
                    >
                      <FaCrop />
                      Crop
                    </RemoveButton>
                    <RemoveButton
                      type="button"
                      onClick={handleRemoveImage}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      <FaTimes />
                      Remove
                    </RemoveButton>
                  </ImageActions>
                </>
              ) : (
                <ImagePlaceholder>
                  <FaImage />
                </ImagePlaceholder>
              )}
            </ImagePreview>
          </FormGroup>

          <FormGroup>
            <Label>Full Name</Label>
            <Input
              type="text"
              value={formData.name}
              onChange={(e) => handleChange('name', e.target.value)}
              placeholder="Your full name"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>Professional Title</Label>
            <Input
              type="text"
              value={formData.title}
              onChange={(e) => handleChange('title', e.target.value)}
              placeholder="Data Analyst & BI Developer"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>Location</Label>
            <Input
              type="text"
              value={formData.location}
              onChange={(e) => handleChange('location', e.target.value)}
              placeholder="City, Country"
            />
          </FormGroup>

          <FormGroup>
            <Label>Email</Label>
            <Input
              type="email"
              value={formData.email}
              onChange={(e) => handleChange('email', e.target.value)}
              placeholder="your.email@example.com"
            />
          </FormGroup>

          <FormGroup>
            <Label>Phone</Label>
            <Input
              type="tel"
              value={formData.phone}
              onChange={(e) => handleChange('phone', e.target.value)}
              placeholder="+1 (555) 123-4567"
            />
          </FormGroup>

          <FormGroup>
            <Label>Bio</Label>
            <TextArea
              value={formData.bio}
              onChange={(e) => handleChange('bio', e.target.value)}
              placeholder="Tell us about yourself, your expertise, and what makes you unique..."
              rows={4}
            />
          </FormGroup>

          <ButtonGroup>
            <Button
              type="submit"
              className="primary"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaSave />
              Save Profile
            </Button>
          </ButtonGroup>
        </Form>
      </FormContainer>

      {showCropper && (
        <ImageCropper
          imageSrc={imageToCrop}
          onCropComplete={handleCropComplete}
          onClose={handleCropCancel}
        />
      )}
    </EditorContainer>
  );
};

export default ProfileEditor;
