import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaPlus, FaEdit, FaTrash, FaSave, FaTimes } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

interface Skill {
  name: string;
  level: number;
  category: 'Technical' | 'Analytical' | 'Tools' | 'Soft Skills';
}

const EditorContainer = styled.div`
  h2 {
    color: #333;
    margin-bottom: 1.5rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }
`;

const SkillsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 1rem;
  margin-bottom: 2rem;
`;

const SkillCard = styled(motion.div)`
  background: #f8f9fa;
  border: 1px solid #e9ecef;
  border-radius: 10px;
  padding: 1.5rem;
  transition: all 0.3s ease;

  &:hover {
    border-color: #ffd700;
    box-shadow: 0 5px 15px rgba(255, 215, 0, 0.1);
  }
`;

const SkillHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const SkillName = styled.h3`
  color: #333;
  font-size: 1.1rem;
  margin: 0;
`;

const SkillCategory = styled.span`
  background: #e9ecef;
  color: #495057;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  font-size: 0.8rem;
`;

const SkillLevel = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem;
`;

const LevelBar = styled.div`
  flex: 1;
  height: 8px;
  background: #e9ecef;
  border-radius: 4px;
  overflow: hidden;
`;

const LevelFill = styled.div<{ level: number }>`
  height: 100%;
  background: linear-gradient(90deg, #ffd700, #ffed4e);
  width: ${props => props.level}%;
  transition: width 0.3s ease;
`;

const LevelText = styled.span`
  font-weight: 600;
  color: #333;
  min-width: 40px;
  text-align: right;
`;

const SkillActions = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ActionButton = styled(motion.button)<{ variant?: 'edit' | 'delete' | 'add' }>`
  padding: 0.5rem;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  
  ${props => {
    switch (props.variant) {
      case 'edit':
        return `
          background: #007bff;
          color: white;
          &:hover { background: #0056b3; }
        `;
      case 'delete':
        return `
          background: #dc3545;
          color: white;
          &:hover { background: #c82333; }
        `;
      case 'add':
        return `
          background: #28a745;
          color: white;
          &:hover { background: #218838; }
        `;
      default:
        return `
          background: #6c757d;
          color: white;
          &:hover { background: #5a6268; }
        `;
    }
  }}
`;

const FormContainer = styled(motion.div)`
  background: #f8f9fa;
  border: 1px solid #e9ecef;
  border-radius: 10px;
  padding: 2rem;
  margin-bottom: 2rem;
`;

const Form = styled.form`
  display: grid;
  gap: 1.5rem;
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Label = styled.label`
  font-weight: 600;
  color: #333;
  font-size: 0.9rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #ced4da;
  border-radius: 6px;
  font-size: 1rem;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #ffd700;
    box-shadow: 0 0 0 2px rgba(255, 215, 0, 0.1);
  }
`;

const Select = styled.select`
  padding: 0.75rem;
  border: 1px solid #ced4da;
  border-radius: 6px;
  font-size: 1rem;
  transition: border-color 0.3s ease;
  background: white;

  &:focus {
    outline: none;
    border-color: #ffd700;
    box-shadow: 0 0 0 2px rgba(255, 215, 0, 0.1);
  }
`;

const RangeInput = styled.input`
  width: 100%;
  height: 8px;
  border-radius: 4px;
  background: #e9ecef;
  outline: none;
  -webkit-appearance: none;

  &::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: #ffd700;
    cursor: pointer;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
  }

  &::-moz-range-thumb {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: #ffd700;
    cursor: pointer;
    border: none;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
  }
`;

const LevelDisplay = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 0.5rem;
`;

const FormActions = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: flex-end;
`;

const SaveButton = styled(motion.button)`
  background: #28a745;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 6px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    background: #218838;
    transform: translateY(-2px);
  }
`;

const CancelButton = styled(motion.button)`
  background: #6c757d;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 6px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    background: #5a6268;
    transform: translateY(-2px);
  }
`;

const SkillsEditor: React.FC = () => {
  const { skills, addSkill, updateSkill, deleteSkill } = useData();
  const [isAdding, setIsAdding] = useState(false);
  const [editingSkill, setEditingSkill] = useState<string | null>(null);
  const [formData, setFormData] = useState<Partial<Skill>>({
    name: '',
    level: 50,
    category: 'Technical'
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (isAdding) {
      addSkill(formData as Skill);
    } else if (editingSkill) {
      updateSkill(editingSkill, formData as Skill);
    }
    
    handleClose();
  };

  const handleClose = () => {
    setFormData({
      name: '',
      level: 50,
      category: 'Technical'
    });
    setIsAdding(false);
    setEditingSkill(null);
  };

  const handleEdit = (skill: Skill) => {
    setFormData(skill);
    setEditingSkill(skill.name);
    setIsAdding(false);
  };

  const categories = ['Technical', 'Analytical', 'Tools', 'Soft Skills'];

  return (
    <EditorContainer>
      <h2>Manage Skills</h2>

      <ActionButton
        variant="add"
        onClick={() => setIsAdding(true)}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        style={{ marginBottom: '1rem' }}
      >
        <FaPlus />
        Add New Skill
      </ActionButton>

      {(isAdding || editingSkill) && (
        <FormContainer
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
        >
          <Form onSubmit={handleSubmit}>
            <FormRow>
              <FormGroup>
                <Label>Skill Name</Label>
                <Input
                  type="text"
                  value={formData.name || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>Category</Label>
                <Select
                  value={formData.category || 'Technical'}
                  onChange={(e) => setFormData(prev => ({ ...prev, category: e.target.value as Skill['category'] }))}
                >
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </Select>
              </FormGroup>
            </FormRow>

            <FormGroup>
              <Label>Proficiency Level: {formData.level}%</Label>
              <RangeInput
                type="range"
                min="0"
                max="100"
                value={formData.level || 50}
                onChange={(e) => setFormData(prev => ({ ...prev, level: parseInt(e.target.value) }))}
              />
              <LevelDisplay>
                <span>Beginner (0%)</span>
                <span>Expert (100%)</span>
              </LevelDisplay>
            </FormGroup>

            <FormActions>
              <SaveButton
                type="submit"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaSave />
                {isAdding ? 'Add Skill' : 'Update Skill'}
              </SaveButton>
              <CancelButton
                type="button"
                onClick={handleClose}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaTimes />
                Cancel
              </CancelButton>
            </FormActions>
          </Form>
        </FormContainer>
      )}

      <SkillsGrid>
        {skills.map((skill) => (
          <SkillCard
            key={skill.name}
            whileHover={{ scale: 1.02 }}
            transition={{ duration: 0.2 }}
          >
            <SkillHeader>
              <SkillName>{skill.name}</SkillName>
              <SkillCategory>{skill.category}</SkillCategory>
            </SkillHeader>
            
            <SkillLevel>
              <LevelBar>
                <LevelFill level={skill.level} />
              </LevelBar>
              <LevelText>{skill.level}%</LevelText>
            </SkillLevel>
            
            <SkillActions>
              <ActionButton
                variant="edit"
                onClick={() => handleEdit(skill)}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <FaEdit />
              </ActionButton>
              <ActionButton
                variant="delete"
                onClick={() => deleteSkill(skill.name)}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <FaTrash />
              </ActionButton>
            </SkillActions>
          </SkillCard>
        ))}
      </SkillsGrid>
    </EditorContainer>
  );
};

export default SkillsEditor;
