import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FaMapMarkerAlt, FaEnvelope, FaPhone, FaDownload, FaChevronDown } from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

const liquidFlow = keyframes`
  0% { transform: translateX(-100%) rotate(0deg); }
  100% { transform: translateX(100vw) rotate(360deg); }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(5deg); }
`;


const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  position: relative;
  overflow: hidden;
`;

const HeroContent = styled.div`
  max-width: 1200px;
  width: 100%;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  z-index: 2;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    text-align: center;
    gap: 3rem;
  }
`;

const ProfileSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const ProfileImageContainer = styled(motion.div)`
  width: 300px;
  height: 300px;
  border-radius: 50%;
  overflow: hidden;
  position: relative;
  margin-bottom: 2rem;
  background: linear-gradient(45deg, 
    rgba(64, 224, 255, 0.3) 0%, 
    rgba(100, 200, 255, 0.2) 50%, 
    rgba(64, 224, 255, 0.3) 100%
  );
  backdrop-filter: blur(10px);
  border: 2px solid rgba(64, 224, 255, 0.3);
  box-shadow: 
    0 20px 40px rgba(0, 0, 0, 0.3),
    inset 0 0 20px rgba(64, 224, 255, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, 
      rgba(64, 224, 255, 0.5), 
      rgba(100, 200, 255, 0.3), 
      rgba(64, 224, 255, 0.5)
    );
    border-radius: 50%;
    z-index: -1;
    animation: ${liquidFlow} 8s linear infinite;
  }

  @media (max-width: 768px) {
    width: 250px;
    height: 250px;
  }
`;

const ProfileImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 50%;
  position: relative;
  z-index: 2;
`;

const PieChartContainer = styled(motion.div)`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  z-index: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const PieCenter = styled(motion.div)`
  position: absolute;
  top: 50%;
  left: 50%;
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, #1a1a2e, #16213e);
  border-radius: 50%;
  transform: translate(-50%, -50%);
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #40e0ff;
  font-size: 1.5rem;
  font-weight: bold;
  box-shadow: 
    0 0 30px rgba(64, 224, 255, 0.4),
    inset 0 2px 4px rgba(255, 255, 255, 0.1);
  border: 2px solid rgba(64, 224, 255, 0.3);
  cursor: pointer;
`;

const ProfilePlaceholder = styled.div`
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, 
    rgba(64, 224, 255, 0.2) 0%, 
    rgba(100, 200, 255, 0.1) 100%
  );
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 4rem;
  color: rgba(64, 224, 255, 0.6);
  position: relative;
  z-index: 2;
`;

const FloatingElements = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  pointer-events: none;
`;

const FloatingElement = styled(motion.div)`
  position: absolute;
  color: rgba(64, 224, 255, 0.1);
  font-size: 2rem;
  animation: ${float} 6s ease-in-out infinite;
`;

const TextSection = styled.div`
  color: white;
  position: relative;
`;

const Greeting = styled(motion.h1)`
  font-size: 1.2rem;
  font-weight: 300;
  color: rgba(64, 224, 255, 0.8);
  margin-bottom: 0.5rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  font-weight: 200;
`;

const Name = styled(motion.h2)`
  font-size: 4rem;
  font-weight: 100;
  margin-bottom: 1rem;
  background: linear-gradient(45deg, 
    #ffffff 0%, 
    rgba(64, 224, 255, 0.9) 50%, 
    #ffffff 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.1;
  letter-spacing: -2px;

  @media (max-width: 768px) {
    font-size: 3rem;
  }
`;

const Title = styled(motion.h3)`
  font-size: 1.5rem;
  font-weight: 300;
  color: rgba(255, 255, 255, 0.7);
  margin-bottom: 1.5rem;
  line-height: 1.4;
  font-weight: 200;
`;

const Bio = styled(motion.p)`
  font-size: 1.1rem;
  line-height: 1.8;
  color: rgba(255, 255, 255, 0.6);
  margin-bottom: 2rem;
  max-width: 500px;
  font-weight: 300;
`;

const ContactInfo = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-bottom: 2rem;
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  color: rgba(255, 255, 255, 0.6);
  font-size: 1rem;
  font-weight: 300;
`;

const ContactIcon = styled.div`
  color: rgba(64, 224, 255, 0.8);
  font-size: 1.2rem;
  width: 20px;
  display: flex;
  justify-content: center;
`;

const ActionButtons = styled(motion.div)`
  display: flex;
  gap: 1.5rem;
  flex-wrap: wrap;
  margin-bottom: 3rem;
`;

const Button = styled(motion.a)`
  padding: 1rem 2rem;
  border-radius: 50px;
  text-decoration: none;
  font-weight: 300;
  font-size: 1rem;
  display: flex;
  align-items: center;
  gap: 0.8rem;
  transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  cursor: pointer;
  border: 1px solid transparent;
  position: relative;
  overflow: hidden;

  &.primary {
    background: linear-gradient(45deg, 
      rgba(64, 224, 255, 0.2), 
      rgba(100, 200, 255, 0.1)
    );
    color: white;
    border-color: rgba(64, 224, 255, 0.3);
    backdrop-filter: blur(10px);

    &::before {
      content: '';
      position: absolute;
      top: 0;
      left: -100%;
      width: 100%;
      height: 100%;
      background: linear-gradient(90deg, 
        transparent, 
        rgba(255, 255, 255, 0.1), 
        transparent
      );
      transition: left 0.5s;
    }

    &:hover::before {
      left: 100%;
    }

    &:hover {
      transform: translateY(-3px);
      box-shadow: 0 10px 30px rgba(64, 224, 255, 0.2);
      border-color: rgba(64, 224, 255, 0.5);
    }
  }

  &.secondary {
    background: transparent;
    color: rgba(64, 224, 255, 0.8);
    border-color: rgba(64, 224, 255, 0.3);

    &:hover {
      background: rgba(64, 224, 255, 0.1);
      color: white;
      transform: translateY(-3px);
      border-color: rgba(64, 224, 255, 0.5);
    }
  }
`;

const ScrollIndicator = styled(motion.div)`
  position: absolute;
  bottom: 2rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  color: rgba(64, 224, 255, 0.6);
  font-size: 0.9rem;
  font-weight: 300;
  text-transform: uppercase;
  letter-spacing: 2px;
`;

const ScrollArrow = styled(motion.div)`
  font-size: 1.5rem;
  animation: ${float} 2s ease-in-out infinite;
`;

const Hero: React.FC = () => {
  const { profile } = useData();
  const [showPieChart, setShowPieChart] = useState(true);

  // Pie chart data for data analyst skills
  const pieData = [
    { label: 'SQL', value: 25, color: '#40e0ff' },
    { label: 'Python', value: 20, color: '#ff6b6b' },
    { label: 'Power BI', value: 18, color: '#4ecdc4' },
    { label: 'Excel', value: 15, color: '#45b7d1' },
    { label: 'Tableau', value: 12, color: '#96ceb4' },
    { label: 'R', value: 10, color: '#feca57' },
  ];

  // Calculate SVG path for pie chart with better performance
  const createPieSlice = (startAngle: number, endAngle: number, radius: number, centerX: number, centerY: number) => {
    const start = polarToCartesian(centerX, centerY, radius, startAngle);
    const end = polarToCartesian(centerX, centerY, radius, endAngle);
    const largeArcFlag = endAngle - startAngle <= 180 ? "0" : "1";

    return [
      "M", centerX, centerY,
      "L", start.x, start.y,
      "A", radius, radius, 0, largeArcFlag, 0, end.x, end.y,
      "Z"
    ].join(" ");
  };

  // Create a smooth loading animation using CSS transforms
  const LoadingSpinner = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    width: 200px;
    height: 200px;
    transform: translate(-50%, -50%);
    border: 3px solid rgba(64, 224, 255, 0.1);
    border-top: 3px solid #40e0ff;
    border-radius: 50%;
    animation: spin 1s linear infinite;

    @keyframes spin {
      0% { transform: translate(-50%, -50%) rotate(0deg); }
      100% { transform: translate(-50%, -50%) rotate(360deg); }
    }
  `;

  const LoadingDots = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    display: flex;
    gap: 8px;

    .dot {
      width: 8px;
      height: 8px;
      background: #40e0ff;
      border-radius: 50%;
      animation: pulse 1.4s ease-in-out infinite both;

      &:nth-child(1) { animation-delay: -0.32s; }
      &:nth-child(2) { animation-delay: -0.16s; }
      &:nth-child(3) { animation-delay: 0s; }
    }

    @keyframes pulse {
      0%, 80%, 100% { 
        transform: scale(0);
        opacity: 0.5;
      }
      40% { 
        transform: scale(1);
        opacity: 1;
      }
    }
  `;

  const polarToCartesian = (centerX: number, centerY: number, radius: number, angleInDegrees: number) => {
    const angleInRadians = (angleInDegrees - 90) * Math.PI / 180.0;
    return {
      x: centerX + (radius * Math.cos(angleInRadians)),
      y: centerY + (radius * Math.sin(angleInRadians))
    };
  };

  // Calculate angles for each slice
  let currentAngle = 0;
  const centerX = 120;
  const centerY = 120;
  const radius = 100;

  const pieSlices = pieData.map(slice => {
    const startAngle = currentAngle;
    const endAngle = currentAngle + (slice.value / 100) * 360;
    currentAngle = endAngle;
    return {
      ...slice,
      startAngle,
      endAngle,
      path: createPieSlice(startAngle, endAngle, radius, centerX, centerY)
    };
  });

  const floatingElements = [
    { icon: 'ðŸ’§', x: 10, y: 20, delay: 0 },
    { icon: 'ðŸŒŠ', x: 80, y: 30, delay: 1 },
    { icon: 'ðŸ’Ž', x: 20, y: 70, delay: 2 },
    { icon: 'âœ¨', x: 70, y: 80, delay: 3 },
  ];

  // Switch from pie chart to profile image after 3 seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowPieChart(false);
    }, 3000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <HeroContainer id="home">
      <FloatingElements>
        {floatingElements.map((element, index) => (
          <FloatingElement
            key={index}
            initial={{ opacity: 0, scale: 0 }}
            animate={{ 
              opacity: [0, 0.3, 0],
              scale: [0, 1, 0],
              y: [0, -30, 0]
            }}
            transition={{
              duration: 6,
              delay: element.delay,
              repeat: Infinity,
              repeatDelay: 3
            }}
            style={{
              left: `${element.x}%`,
              top: `${element.y}%`,
            }}
          >
            {element.icon}
          </FloatingElement>
        ))}
      </FloatingElements>

      <HeroContent>
        <ProfileSection>
          <ProfileImageContainer
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ duration: 1, ease: "easeOut" }}
            whileHover={{ scale: 1.05 }}
          >
            {showPieChart ? (
              <>
                <PieChartContainer
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.8, delay: 0.5 }}
                >
                  {/* Smooth SVG loading animation */}
                  <svg width="240" height="240" viewBox="0 0 240 240" style={{ filter: 'drop-shadow(0 4px 8px rgba(0,0,0,0.3))' }}>
                    <defs>
                      {/* Radial gradient for smooth appearance */}
                      <radialGradient id="loadingGradient" cx="50%" cy="50%" r="50%">
                        <stop offset="0%" stopColor="rgba(64, 224, 255, 0.8)" />
                        <stop offset="70%" stopColor="rgba(64, 224, 255, 0.4)" />
                        <stop offset="100%" stopColor="rgba(64, 224, 255, 0.1)" />
                      </radialGradient>

                      {/* Animated gradient for the loading ring */}
                      <linearGradient id="animatedGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                        <stop offset="0%" stopColor="#40e0ff" stopOpacity="0.9">
                          <animate attributeName="stop-opacity" values="0.3;1;0.3" dur="2s" repeatCount="indefinite" />
                        </stop>
                        <stop offset="50%" stopColor="#64c8ff" stopOpacity="0.7">
                          <animate attributeName="stop-opacity" values="0.5;0.9;0.5" dur="2s" repeatCount="indefinite" begin="0.5s" />
                        </stop>
                        <stop offset="100%" stopColor="#40e0ff" stopOpacity="0.5">
                          <animate attributeName="stop-opacity" values="0.2;0.8;0.2" dur="2s" repeatCount="indefinite" begin="1s" />
                        </stop>
                      </linearGradient>
                    </defs>

                    {/* Background circle */}
                    <circle
                      cx="120"
                      cy="120"
                      r="100"
                      fill="url(#loadingGradient)"
                      opacity="0.3"
                    />

                    {/* Animated loading ring */}
                    <circle
                      cx="120"
                      cy="120"
                      r="90"
                      fill="none"
                      stroke="url(#animatedGradient)"
                      strokeWidth="4"
                      strokeLinecap="round"
                      strokeDasharray="565.48"
                      strokeDashoffset="565.48"
                    >
                      <animate
                        attributeName="stroke-dashoffset"
                        values="565.48;0;565.48"
                        dur="2s"
                        repeatCount="indefinite"
                      />
                      <animateTransform
                        attributeName="transform"
                        type="rotate"
                        values="0 120 120;360 120 120"
                        dur="3s"
                        repeatCount="indefinite"
                      />
                    </circle>

                    {/* Inner pulsing circle */}
                    <circle
                      cx="120"
                      cy="120"
                      r="60"
                      fill="none"
                      stroke="rgba(64, 224, 255, 0.6)"
                      strokeWidth="2"
                      opacity="0.8"
                    >
                      <animate
                        attributeName="r"
                        values="60;70;60"
                        dur="1.5s"
                        repeatCount="indefinite"
                      />
                      <animate
                        attributeName="opacity"
                        values="0.8;0.3;0.8"
                        dur="1.5s"
                        repeatCount="indefinite"
                      />
                    </circle>

                    {/* Loading text */}
                    <text
                      x="120"
                      y="120"
                      textAnchor="middle"
                      dominantBaseline="middle"
                      fill="rgba(64, 224, 255, 0.9)"
                      fontSize="14"
                      fontWeight="bold"
                    >
                      <animate attributeName="opacity" values="0.5;1;0.5" dur="1s" repeatCount="indefinite" />
                      Loading...
                    </text>
                  </svg>
                </PieChartContainer>
                <PieCenter
                  initial={{ scale: 0, rotate: -180 }}
                  animate={{ scale: 1, rotate: 0 }}
                  transition={{ duration: 0.8, delay: 2.0, ease: "easeOut" }}
                  whileHover={{ scale: 1.1, rotate: 5 }}
                >
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 0.5, delay: 2.5 }}
                  >
                    %
                  </motion.div>
                </PieCenter>
              </>
            ) : (
              <motion.div
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.8 }}
                style={{ width: '100%', height: '100%' }}
              >
                {profile.profileImage && profile.profileImage !== '/api/placeholder/300/300' ? (
                  <ProfileImage src={profile.profileImage} alt={profile.name} />
                ) : (
                  <ProfilePlaceholder>
                    ðŸ’§
                  </ProfilePlaceholder>
                )}
              </motion.div>
            )}
          </ProfileImageContainer>
        </ProfileSection>

        <TextSection>
          <Greeting
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Hello, I'm
          </Greeting>

          <Name
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            {profile.name || 'Your Name'}
          </Name>

          <Title
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            {profile.title || 'Data Analyst & BI Developer'}
          </Title>

          <Bio
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            {profile.bio || 'Passionate data analyst with expertise in transforming complex data into actionable insights. Specializing in business intelligence, machine learning, and data visualization to drive strategic decision-making.'}
          </Bio>

          <ContactInfo
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            {profile.location && (
              <ContactItem>
                <ContactIcon><FaMapMarkerAlt /></ContactIcon>
                <span>{profile.location}</span>
              </ContactItem>
            )}
            {profile.email && (
              <ContactItem>
                <ContactIcon><FaEnvelope /></ContactIcon>
                <span>{profile.email}</span>
              </ContactItem>
            )}
            {profile.phone && (
              <ContactItem>
                <ContactIcon><FaPhone /></ContactIcon>
                <span>{profile.phone}</span>
              </ContactItem>
            )}
          </ContactInfo>

          <ActionButtons
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1 }}
          >
            <Button
              className="primary"
              href="#projects"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              View My Work
            </Button>
            <Button
              className="secondary"
              href="#contact"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaDownload />
              Download CV
            </Button>
          </ActionButtons>
        </TextSection>
      </HeroContent>

      <ScrollIndicator
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 2 }}
      >
        <span>Scroll to explore</span>
        <ScrollArrow>
          <FaChevronDown />
        </ScrollArrow>
      </ScrollIndicator>
    </HeroContainer>
  );
};

export default Hero;
