import React, { useRef, useEffect, useState } from 'react';
import styled from 'styled-components';
import { motion, useScroll, useTransform, useInView } from 'framer-motion';

const SkillTreeContainer = styled.section`
  min-height: 100vh;
  padding: 4rem 2rem;
  background: linear-gradient(135deg, 
    rgba(10, 10, 30, 0.95) 0%, 
    rgba(20, 20, 50, 0.9) 50%, 
    rgba(10, 10, 30, 0.95) 100%
  );
  position: relative;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const SkillTreeWrapper = styled.div`
  max-width: 1200px;
  width: 100%;
  position: relative;
  perspective: 1000px;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: 300;
  text-align: center;
  margin-bottom: 3rem;
  background: linear-gradient(45deg, #40e0ff, #ff6b6b, #4ecdc4);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 0 0 30px rgba(64, 224, 255, 0.3);
`;

const SkillTreeGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 2rem;
  position: relative;
  transform-style: preserve-3d;
`;

const SkillNode = styled(motion.div)<{ 
  level: number; 
  category: string; 
  isUnlocked: boolean;
  position: { x: number; y: number };
}>`
  position: relative;
  width: 120px;
  height: 120px;
  border-radius: 50%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transform-style: preserve-3d;
  transition: all 0.3s ease;
  
  background: ${props => props.isUnlocked 
    ? `linear-gradient(135deg, ${getCategoryColor(props.category)}, ${getCategoryColor(props.category, 0.7)})`
    : 'linear-gradient(135deg, rgba(60, 60, 60, 0.3), rgba(40, 40, 40, 0.2))'
  };
  
  border: 2px solid ${props => props.isUnlocked 
    ? getCategoryColor(props.category, 0.8)
    : 'rgba(100, 100, 100, 0.3)'
  };
  
  box-shadow: ${props => props.isUnlocked 
    ? `0 10px 30px ${getCategoryColor(props.category, 0.4)}, inset 0 0 20px rgba(255, 255, 255, 0.1)`
    : '0 5px 15px rgba(0, 0, 0, 0.3)'
  };
  
  grid-column: ${props => props.position.x};
  grid-row: ${props => props.position.y};
  
  &:hover {
    transform: translateZ(20px) scale(1.1);
    box-shadow: ${props => props.isUnlocked 
      ? `0 20px 40px ${getCategoryColor(props.category, 0.6)}, inset 0 0 30px rgba(255, 255, 255, 0.2)`
      : '0 10px 25px rgba(0, 0, 0, 0.5)'
    };
  }
`;

const SkillIcon = styled.div<{ category: string }>`
  font-size: 2rem;
  margin-bottom: 0.5rem;
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3));
`;

const SkillName = styled.div<{ isUnlocked: boolean }>`
  font-size: 0.8rem;
  font-weight: 600;
  text-align: center;
  color: ${props => props.isUnlocked ? '#ffffff' : 'rgba(150, 150, 150, 0.6)'};
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
`;

const SkillLevel = styled.div<{ isUnlocked: boolean }>`
  font-size: 0.7rem;
  color: ${props => props.isUnlocked ? 'rgba(255, 255, 255, 0.8)' : 'rgba(150, 150, 150, 0.4)'};
  margin-top: 0.2rem;
`;

const ConnectionLine = styled(motion.div)<{ 
  from: { x: number; y: number }; 
  to: { x: number; y: number };
  category: string;
}>`
  position: absolute;
  height: 2px;
  background: linear-gradient(90deg, 
    ${props => getCategoryColor(props.category, 0.8)}, 
    ${props => getCategoryColor(props.category, 0.4)}
  );
  box-shadow: 0 0 10px ${props => getCategoryColor(props.category, 0.5)};
  z-index: 1;
  
  left: ${props => props.from.x * 25}%;
  top: ${props => props.from.y * 25}%;
  width: ${props => Math.abs(props.to.x - props.from.x) * 25}%;
  transform-origin: left center;
  transform: rotate(${props => Math.atan2(props.to.y - props.from.y, props.to.x - props.from.x) * 180 / Math.PI}deg);
`;

const CategoryTitle = styled(motion.h3)<{ category: string }>`
  font-size: 1.5rem;
  font-weight: 600;
  text-align: center;
  margin: 2rem 0 1rem 0;
  color: ${props => getCategoryColor(props.category)};
  text-shadow: 0 0 20px ${props => getCategoryColor(props.category, 0.5)};
  grid-column: 1 / -1;
`;

const FloatingParticles = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 0;
`;

const Particle = styled(motion.div)<{ delay: number }>`
  position: absolute;
  width: 4px;
  height: 4px;
  background: radial-gradient(circle, #40e0ff, transparent);
  border-radius: 50%;
  box-shadow: 0 0 10px #40e0ff;
`;

// Helper function to get category colors
const getCategoryColor = (category: string, opacity: number = 1) => {
  const colors = {
    'Technical': `rgba(64, 224, 255, ${opacity})`,
    'Analytical': `rgba(255, 107, 107, ${opacity})`,
    'Tools': `rgba(78, 205, 196, ${opacity})`,
    'Soft Skills': `rgba(255, 193, 7, ${opacity})`,
    'Data Science': `rgba(156, 39, 176, ${opacity})`,
    'Visualization': `rgba(76, 175, 80, ${opacity})`
  };
  return colors[category as keyof typeof colors] || `rgba(100, 100, 100, ${opacity})`;
};

// Skill data structure
const skillTreeData = {
  'Technical': [
    { name: 'SQL', level: 95, icon: '🗄️', position: { x: 1, y: 1 }, prerequisites: [] },
    { name: 'Python', level: 90, icon: '🐍', position: { x: 2, y: 1 }, prerequisites: ['SQL'] },
    { name: 'R', level: 85, icon: '📊', position: { x: 3, y: 1 }, prerequisites: ['SQL'] },
