const SkillTree: React.FC = () => {
  const containerRef = useRef<HTMLDivElement>(null);
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start end", "end start"]
  });
  
  const [unlockedSkills, setUnlockedSkills] = useState<string[]>(['SQL', 'Excel', 'Statistics']);
  const [hoveredSkill, setHoveredSkill] = useState<string | null>(null);
  
  const isInView = useInView(containerRef, { once: true, margin: "-100px" });
  
  // 3D transforms based on scroll
  const rotateX = useTransform(scrollYProgress, [0, 0.5, 1], [0, 15, 0]);
  const rotateY = useTransform(scrollYProgress, [0, 0.5, 1], [0, 10, 0]);
  const scale = useTransform(scrollYProgress, [0, 0.3, 0.7, 1], [0.8, 1, 1, 0.8]);
  const translateZ = useTransform(scrollYProgress, [0, 0.5, 1], [0, 50, 0]);
  
  // Check if skill should be unlocked
  const isSkillUnlocked = (skillName: string, prerequisites: string[]) => {
    return prerequisites.every(prereq => unlockedSkills.includes(prereq));
  };
  
  // Handle skill click
  const handleSkillClick = (skillName: string, prerequisites: string[]) => {
    if (isSkillUnlocked(skillName, prerequisites)) {
      setUnlockedSkills(prev => 
        prev.includes(skillName) 
          ? prev.filter(skill => skill !== skillName)
          : [...prev, skillName]
      );
    }
  };
  
  // Generate floating particles
  const particles = Array.from({ length: 20 }, (_, i) => ({
    id: i,
    x: Math.random() * 100,
    y: Math.random() * 100,
    delay: Math.random() * 5
  }));
  
  return (
    <SkillTreeContainer ref={containerRef} id="skill-tree">
      <FloatingParticles>
        {particles.map(particle => (
          <Particle
            key={particle.id}
            delay={particle.delay}
            initial={{ 
              x: `${particle.x}%`, 
              y: `${particle.y}%`,
              opacity: 0,
              scale: 0
            }}
            animate={{ 
              opacity: [0, 1, 0],
              scale: [0, 1, 0],
              y: [`${particle.y}%`, `${particle.y - 20}%`, `${particle.y}%`]
            }}
            transition={{
              duration: 3,
              delay: particle.delay,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        ))}
      </FloatingParticles>
      
      <SkillTreeWrapper>
        <SectionTitle
          initial={{ opacity: 0, y: 50 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
          transition={{ duration: 0.8 }}
        >
          Skill Tree
        </SectionTitle>
        
        <motion.div
          style={{
            rotateX,
            rotateY,
            scale,
            translateZ,
            transformStyle: 'preserve-3d'
          }}
        >
          {Object.entries(skillTreeData).map(([category, skills]) => (
            <React.Fragment key={category}>
              <CategoryTitle
                category={category}
                initial={{ opacity: 0, x: -50 }}
                animate={isInView ? { opacity: 1, x: 0 } : { opacity: 0, x: -50 }}
                transition={{ duration: 0.6, delay: 0.2 }}
              >
                {category}
              </CategoryTitle>
              
              <SkillTreeGrid>
                {skills.map((skill, index) => {
                  const isUnlocked = isSkillUnlocked(skill.name, skill.prerequisites);
                  
                  return (
                    <React.Fragment key={skill.name}>
                      <SkillNode
                        level={index + 1}
                        category={category}
                        isUnlocked={isUnlocked}
                        position={skill.position}
                        initial={{ 
                          opacity: 0, 
                          scale: 0,
                          rotateX: -90,
                          rotateY: 45
                        }}
                        animate={isInView ? { 
                          opacity: 1, 
                          scale: 1,
                          rotateX: 0,
                          rotateY: 0
                        } : { 
                          opacity: 0, 
                          scale: 0,
                          rotateX: -90,
                          rotateY: 45
                        }}
                        transition={{ 
                          duration: 0.6, 
                          delay: 0.3 + index * 0.1,
                          type: "spring",
                          stiffness: 100
                        }}
                        whileHover={{ 
                          scale: 1.1,
                          rotateY: 10,
                          rotateX: 5
                        }}
                        whileTap={{ scale: 0.95 }}
                        onClick={() => handleSkillClick(skill.name, skill.prerequisites)}
                        onHoverStart={() => setHoveredSkill(skill.name)}
                        onHoverEnd={() => setHoveredSkill(null)}
                      >
                        <SkillIcon category={category}>
                          {skill.icon}
                        </SkillIcon>
                        <SkillName isUnlocked={isUnlocked}>
                          {skill.name}
                        </SkillName>
                        <SkillLevel isUnlocked={isUnlocked}>
                          Lv.{skill.level}
                        </SkillLevel>
                      </SkillNode>
                      
                      {/* Connection lines */}
                      {skill.prerequisites.map(prereq => {
                        const prereqSkill = skills.find(s => s.name === prereq);
                        if (!prereqSkill) return null;
                        
                        return (
                          <ConnectionLine
                            key={`${prereq}-${skill.name}`}
                            from={prereqSkill.position}
                            to={skill.position}
                            category={category}
                            initial={{ scaleX: 0 }}
                            animate={isInView ? { scaleX: 1 } : { scaleX: 0 }}
                            transition={{ duration: 0.8, delay: 0.5 + index * 0.1 }}
                          />
                        );
                      })}
                    </React.Fragment>
                  );
                })}
              </SkillTreeGrid>
            </React.Fragment>
          ))}
        </motion.div>
      </SkillTreeWrapper>
    </SkillTreeContainer>
  );
};

export default SkillTree;
