import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';
import { 
  FaCode, 
  FaCloud,
  FaDatabase,
  FaChartBar,
  FaHandshake
} from 'react-icons/fa';
import { useData } from '../contexts/DataContext';

const SkillsContainer = styled.section`
  padding: 6rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: 100;
  color: white;
  text-align: center;
  margin-bottom: 1rem;
  background: linear-gradient(45deg, #ffffff, rgba(64, 224, 255, 0.9), #ffffff);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -2px;
`;

const SectionSubtitle = styled(motion.p)`
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.8);
  text-align: center;
  margin-bottom: 4rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const SkillsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-bottom: 4rem;
`;

const SkillCategory = styled(motion.div)<{ category: string }>`
  background: ${props => {
    switch (props.category) {
      case 'Programming Languages': return 'rgba(25, 118, 210, 0.05)';
      case 'Data Platforms': return 'rgba(123, 31, 162, 0.05)';
      case 'Systems & Databases': return 'rgba(56, 142, 60, 0.05)';
      case 'Analytical Tools': return 'rgba(245, 124, 0, 0.05)';
      case 'Soft Skills': return 'rgba(194, 24, 91, 0.05)';
      default: return 'rgba(64, 224, 255, 0.05)';
    }
  }};
  border: 1px solid ${props => {
    switch (props.category) {
      case 'Programming Languages': return 'rgba(25, 118, 210, 0.2)';
      case 'Data Platforms': return 'rgba(123, 31, 162, 0.2)';
      case 'Systems & Databases': return 'rgba(56, 142, 60, 0.2)';
      case 'Analytical Tools': return 'rgba(245, 124, 0, 0.2)';
      case 'Soft Skills': return 'rgba(194, 24, 91, 0.2)';
      default: return 'rgba(64, 224, 255, 0.2)';
    }
  }};
  border-radius: 30px;
  padding: 2rem;
  backdrop-filter: blur(20px);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
  transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: ${props => {
      switch (props.category) {
        case 'Programming Languages': return 'linear-gradient(45deg, rgba(25, 118, 210, 0.1), transparent, rgba(66, 165, 245, 0.1))';
        case 'Data Platforms': return 'linear-gradient(45deg, rgba(123, 31, 162, 0.1), transparent, rgba(186, 104, 200, 0.1))';
        case 'Systems & Databases': return 'linear-gradient(45deg, rgba(56, 142, 60, 0.1), transparent, rgba(102, 187, 106, 0.1))';
        case 'Analytical Tools': return 'linear-gradient(45deg, rgba(245, 124, 0, 0.1), transparent, rgba(255, 183, 77, 0.1))';
        case 'Soft Skills': return 'linear-gradient(45deg, rgba(194, 24, 91, 0.1), transparent, rgba(240, 98, 146, 0.1))';
        default: return 'linear-gradient(45deg, rgba(64, 224, 255, 0.1), transparent, rgba(100, 200, 255, 0.1))';
      }
    }};
    opacity: 0;
    transition: opacity 0.4s ease;
    z-index: 1;
  }

  &:hover {
    transform: translateY(-10px) scale(1.02);
    border-color: ${props => {
      switch (props.category) {
        case 'Programming Languages': return 'rgba(25, 118, 210, 0.4)';
        case 'Data Platforms': return 'rgba(123, 31, 162, 0.4)';
        case 'Systems & Databases': return 'rgba(56, 142, 60, 0.4)';
        case 'Analytical Tools': return 'rgba(245, 124, 0, 0.4)';
        case 'Soft Skills': return 'rgba(194, 24, 91, 0.4)';
        default: return 'rgba(64, 224, 255, 0.4)';
      }
    }};
    box-shadow: ${props => {
      switch (props.category) {
        case 'Programming Languages': return '0 25px 50px rgba(25, 118, 210, 0.2)';
        case 'Data Platforms': return '0 25px 50px rgba(123, 31, 162, 0.2)';
        case 'Systems & Databases': return '0 25px 50px rgba(56, 142, 60, 0.2)';
        case 'Analytical Tools': return '0 25px 50px rgba(245, 124, 0, 0.2)';
