const Skills: React.FC = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });
  const { skills } = useData();

  // Group skills by category
  const skillsByCategory = skills.reduce((acc, skill) => {
    if (!acc[skill.category]) {
      acc[skill.category] = [];
    }
    acc[skill.category].push(skill);
    return acc;
  }, {} as Record<string, any[]>);

  const categoryIcons = {
    'Programming Languages': <FaCode />,
    'Data Platforms': <FaCloud />,
    'Systems & Databases': <FaDatabase />,
    'Analytical Tools': <FaChartBar />,
    'Soft Skills': <FaHandshake />
  };

  const categoryOrder = ['Programming Languages', 'Data Platforms', 'Systems & Databases', 'Analytical Tools', 'Soft Skills'];

  // Get top skills for progress section (highest levels)
  const progressSkills = skills
    .sort((a, b) => b.level - a.level)
    .slice(0, 6);

  return (
    <SkillsContainer id="skills" ref={ref}>
      <SectionTitle
        initial={{ opacity: 0, y: 30 }}
        animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.6 }}
      >
        Technical Skills
      </SectionTitle>

      <SectionSubtitle
        initial={{ opacity: 0, y: 30 }}
        animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.6, delay: 0.2 }}
      >
        Comprehensive expertise in data analysis, visualization, and business intelligence
      </SectionSubtitle>

      <SkillsGrid>
        {categoryOrder.map((categoryName, index) => {
          const categorySkills = skillsByCategory[categoryName] || [];
          if (categorySkills.length === 0) return null;

          return (
            <SkillCategory
              key={categoryName}
              category={categoryName}
              initial={{ opacity: 0, y: 50, rotateX: -15 }}
              animate={isInView ? { 
                opacity: 1, 
                y: 0, 
                rotateX: 0,
                scale: 1
              } : { 
                opacity: 0, 
                y: 50, 
                rotateX: -15,
                scale: 0.9
              }}
              transition={{ 
                duration: 0.8, 
                delay: index * 0.2,
                ease: "easeOut"
              }}
              whileHover={{ 
                scale: 1.05,
                rotateY: 5,
                z: 50
              }}
              whileTap={{ scale: 0.95 }}
            >
              <CategoryTitle>
                <CategoryIcon category={categoryName}>
                  {categoryIcons[categoryName as keyof typeof categoryIcons]}
                </CategoryIcon>
                {categoryName}
              </CategoryTitle>
              <SkillsList>
                {categorySkills.map((skill, skillIndex) => (
                  <SkillItem
                    key={skill.name}
                    category={categoryName}
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={isInView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                    transition={{ 
                      duration: 0.3, 
                      delay: index * 0.1 + skillIndex * 0.05 
                    }}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    {skill.name}
                  </SkillItem>
                ))}
              </SkillsList>
            </SkillCategory>
          );
        })}
      </SkillsGrid>

      <ProgressSection
        as={motion.div}
        initial={{ opacity: 0, y: 30 }}
        animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.6, delay: 0.4 }}
      >
        <ProgressTitle>Proficiency Levels</ProgressTitle>
        {progressSkills.map((skill: any, index: number) => (
          <ProgressItem
            key={index}
            initial={{ opacity: 0, x: -30 }}
            animate={isInView ? { opacity: 1, x: 0 } : { opacity: 0, x: -30 }}
            transition={{ duration: 0.6, delay: 0.6 + index * 0.1 }}
          >
            <ProgressLabel>
              <span>{skill.name}</span>
              <span>{skill.level}%</span>
            </ProgressLabel>
            <ProgressBar>
              <ProgressFill
                percentage={skill.level}
                initial={{ width: 0 }}
                animate={isInView ? { width: `${skill.level}%` } : { width: 0 }}
                transition={{ duration: 1, delay: 0.8 + index * 0.1 }}
              />
            </ProgressBar>
          </ProgressItem>
        ))}
      </ProgressSection>
    </SkillsContainer>
  );
};

export default Skills;
