    { label: 'Tableau', value: 12, color: '#96ceb4' },
    { label: 'R', value: 10, color: '#feca57' },
  ];

  // Calculate SVG path for pie chart
  const createPieSlice = (startAngle: number, endAngle: number, radius: number) => {
    const start = polarToCartesian(radius, radius, radius, endAngle);
    const end = polarToCartesian(radius, radius, radius, startAngle);
    const largeArcFlag = endAngle - startAngle <= 180 ? "0" : "1";

    return [
      "M", radius, radius,
      "L", start.x, start.y,
      "A", radius, radius, 0, largeArcFlag, 0, end.x, end.y,
      "Z"
    ].join(" ");
  };

  const polarToCartesian = (centerX: number, centerY: number, radius: number, angleInDegrees: number) => {
    const angleInRadians = (angleInDegrees - 90) * Math.PI / 180.0;
    return {
      x: centerX + (radius * Math.cos(angleInRadians)),
      y: centerY + (radius * Math.sin(angleInRadians))
    };
  };

  // Calculate angles for each slice
  let currentAngle = 0;
  const pieSlices = pieData.map(slice => {
    const startAngle = currentAngle;
    const endAngle = currentAngle + (slice.value / 100) * 360;
    currentAngle = endAngle;
